/// <summary>
///     Gets the related product of BOM Version control for validating and
///  selecting BOM Version.
/// </summary>
/// <param name="_bomVersionCtl">
///     BOM Version control.
/// </param>
/// <returns>
///     ItemId of product.
/// </returns>
private ItemIdProduction getRelatedProductOfBOMVersion(FormStringControl _bomVersionCtl)
{
    ItemIdProduction        itemId;
    FormStringControl       prodCtl;
    int                     indexNumber;

    if (_bomVersionCtl)
    {
        // get the matched product ctl
        indexNumber = element.getControlIndexNumber(_bomVersionCtl.name());
        if (indexNumber > 0)
        {
            prodCtl = element.design().controlName(strfmt('%1%2', #ProductCtlNamePrefix, indexNumber));

            if (prodCtl)
            {
                itemId = prodCtl.text();
            }
        }
    }

    return itemId;
}

/// <summary>
///     Does some initialization.
/// </summary>
public void init()
{
    Set             validDataSources;
    InventDimParm   inventDimParmShow;
    InventDimParm   inventDimParmEnabled;

    xSysLastValue::getLast(this);

    super();

    //init last values
    this.initLastValues();

    // init inventDimFormSetupBOM
    validDataSources = new Set(Types::String);

    validDataSources.add(bom1_ds.name());
    validDataSources.add(inventDimBOM1_DS.name());
    validDataSources.add(bom2_ds.name());
    validDataSources.add(inventDimBOM2_DS.name());
    validDataSources.add(bom3_ds.name());
    validDataSources.add(inventDimBOM3_DS.name());
    validDataSources.add(bom4_ds.name());
    validDataSources.add(inventDimBOM4_DS.name());
    validDataSources.add(bom5_ds.name());
    validDataSources.add(inventDimBOM5_DS.name());

    inventDimFormSetupBOM = InventDimCtrl::newFromFormValidDataSources(element,validDataSources);
    inventDimFormSetupBOM.parmSkipOnHandLookUp(true);
    inventDimParmShow.initDimBOMAll();
    inventDimParmShow.InventSiteIdFlag = false;
    inventDimFormSetupBOM.parmDimParmVisibleGrid(inventDimParmShow);

    inventDimFormSetupBOM.parmDimParmEnabled(inventDimParmEnabled);
    inventDimFormSetupBOM.formSetControls(true);
}

    public void initLastValues()
{
    Counter             i;
    FormStringControl   bomVersionCtl;
    FormStringControl   productCtl;

    // set comparison last value
    compareField.selection(comparisonType);
    costType.selection(comparisonCostType);

    // set bom version last values and product last values
    for (i = 1; i <= InventBOMComparisonTmp_CN::getMaxComparisonNum(); i++)
    {
        productCtl      = element.design().controlName(strfmt('%1%2', #ProductCtlNamePrefix, i));
        if (productCtl
            && productItems)
        {
            productCtl.text(productItems.value(i));
        }

        bomVersionCtl   = element.design().controlName(strfmt('%1%2', #BOMVersionCtlNamePrefix, i));

        if (bomVersionCtl
            && bomVersions)
        {
            bomVersionCtl.text(bomVersions.value(i));
        }
    }
}

public void initParmDefault()
{
    comparisonType       = InventBOMComparisonType_CN::Quantity;
    comparisonCostType   = InventBOMComparisonCostType_CN::InventoryCost;
}

