/// <summary>
///     Opens a lookup form for a BOM Version control
/// </summary>
/// <param name="_bomVersionCtl">
///     BOM Version control.
/// </param>
public void lookupBOMVersion(FormStringControl _bomVersionCtl)
{
    ItemIdProduction    itemId;

    if (_bomVersionCtl)
    {
        // get the matched product id
        itemId = element.getRelatedProductOfBOMVersion(_bomVersionCtl);

        // open look up form
        BOMTable::lookupProductBOMId_CN(_bomVersionCtl, itemId);
    }
}

    public container pack()
{
    return [#CurrentVersion, #CurrentList, bomVersions.pack(), productItems.pack()];
}

    private void refreshBOMComparisons()
{
    this.refreshBOMTabs();

    inventBOMComparisonTmp_CN_ds.removeFilter();
    inventBOMComparisonTmp_CN_ds.research();

    printMenu.enabled(inventBOMComparisonTmp_CN_ds.getPosition() > 0);
    tabOverview.activatePage();
}

/// <summary>
///     Hides the unused controls whose corresponding BOM ID is not selected.
/// And update the used data sources.
/// </summary>
private void refreshBOMTabs()
{
    int                 i;
    FormRealControl     fieldCtl;
    FormTabPageControl  bomTabPageCtl;

    for (i = 1; i <= InventBOMComparisonTmp_CN::getMaxComparisonNum(); i++)
    {
        fieldCtl        = element.design().controlName(strfmt('%1%2', #BOMQuantityFieldNamePrefix, i));
        bomTabPageCtl   = element.design().controlName(strfmt('%1%2', #BOMTabpageNamePrefix, i));

        if (fieldCtl && bomTabPageCtl)
        {
            // the label should be corrected before visible.
            if (bomVersions.value(i) == '')
            {
                fieldCtl.visible(false);
                bomTabPageCtl.visible(false);
            }
            else
            {
                fieldCtl.label(bomVersions.value(i));
                fieldCtl.visible(true);

                bomTabPageCtl.caption(bomVersions.value(i));
                bomTabPageCtl.visible(true);

                this.updateBomDS(i);
            }
        }
    }
}

/// <summary>
///     Updates the status of MenuButton:Invent. If Overview tab page actived, disable
/// the button, otherwise, enable it.
/// </summary>
/// <param name="_bomTabPageCtl">
///     The actived tab page control
/// </param>
public void tabPageActived(FormTabPageControl _bomTabPageCtl)
{
    if (_bomTabPageCtl
        && _bomTabPageCtl.name() == #OverviewTabPage)
    {
        invent.enabled(false);
    }
    else
    {
        invent.enabled(true);
    }
}

    public boolean unpack(container packedClass)
{
    Integer     version     = conpeek(packedClass,1);
    container   packedBoms;
    container   packedProducts;

    switch (version)
    {
        case #CurrentVersion:
            [version, #CurrentList, packedBoms, packedProducts] = packedClass;

            if (packedBoms != connull())
            {
                bomVersions = Array::create(packedBoms);
            }

            if (packedProducts != connull())
            {
               productItems = Array::create(packedProducts);
            }
            break;
        default:
            return false;
    }

    return true;
}

/// <summary>
///     Updates the datasource of specified data source named like BOM*. * is the _index.
/// </summary>
/// <param name="_index">
///     Index number of data source. From 1 to MaxComparisonNum (Here is 5).
/// </param>
private void updateBomDS(int _index)
{
    if (_index >= 1 && _index <= InventBOMComparisonTmp_CN::getMaxComparisonNum())
    {
        switch (_index)
        {
            case 1:
                bom1_ds.query().dataSourceTable(tablenum(BOM)).findRange(fieldnum(BOM, bomId)).value(SysQuery::value(bomVersions.value(_index)));
                bom1_ds.executeQuery();
                break;
            case 2:
                bom2_ds.query().dataSourceTable(tablenum(BOM)).findRange(fieldnum(BOM, bomId)).value(SysQuery::value(bomVersions.value(_index)));
                bom2_ds.executeQuery();
                break;
            case 3:
                bom3_ds.query().dataSourceTable(tablenum(BOM)).findRange(fieldnum(BOM, bomId)).value(SysQuery::value(bomVersions.value(_index)));
                bom3_ds.executeQuery();
                break;
            case 4:
                bom4_ds.query().dataSourceTable(tablenum(BOM)).findRange(fieldnum(BOM, bomId)).value(SysQuery::value(bomVersions.value(_index)));
                bom4_ds.executeQuery();
                break;
            case 5:
                bom5_ds.query().dataSourceTable(tablenum(BOM)).findRange(fieldnum(BOM, bomId)).value(SysQuery::value(bomVersions.value(_index)));
                bom5_ds.executeQuery();
                break;
        }
    }
}

/// <summary>
///     Validates the content of a BOM Version control. If its corresponding product
///  is selected, check if it is a BOM Id related to the product. If not selected,
///  check if it is a valid BOM Id. If not valid, clear its content.
/// </summary>
/// <param name="_bomVersionCtl">
///     BOM Version control
/// </param>
/// <returns>
///     Returns true, if its content is valid.
/// </returns>
/// <remarks>
///     If its content is not valid, clear its content.
/// </remarks>
public boolean validateBOMVersion(FormStringControl _bomVersionCtl)
{
    boolean             ret = false;
    BOMId               bomId;
    ItemIdProduction    itemId;

    if (_bomVersionCtl)
    {
        bomId = _bomVersionCtl.text();

        // get the matched product id
        itemId = this.getRelatedProductOfBOMVersion(_bomVersionCtl);

        ret = InventBOMComparison_CN::validateBOMVersion(bomId, itemId);

        if (ret
            && _bomVersionCtl.text())
        {
            // As the typed in BOM Id may contain some space at the end
            // and pass the validation. But such BOM Id is not really valid,
            // we need to get the corresponding valid BOM Id.
            _bomVersionCtl.text(BOMTable::find(bomId).bomId);
        }
        else
        {
            // reset
            _bomVersionCtl.text('');
        }
    }

    return ret;
}

/// <summary>
///     Checks the content of a Product control whether it is a
///  valid product ID. If not, clear its content.
/// </summary>
/// <param name="_productCtl">
///     Product control.
/// </param>
/// <returns>
///     Returns true, if its content is a valid product ID.
/// </returns>
/// <remarks>
///     If its content is not valid, clear its content. Otherwise,
///  clear its corresponding BOM Version control.
/// </remarks>
public boolean validateProduct(FormStringControl _productCtl)
{
    boolean ret = false;

    if (_productCtl)
    {
        ret = InventBOMComparison_CN::validateProduct(_productCtl.text());

        if (ret
            && _productCtl.text())
        {
            // Clear its corresponding BOM Version control if its valid
            this.clearMatchedBOMVersionCtl(_productCtl);
        }
        else
        {
            // reset
            _productCtl.text('');
        }
    }

    return ret;
}

