[Form]
public class InventBatch extends FormRun
{
    boolean                             jumpRefMode;
    boolean                             return2JumpRef;
    ItemId                              itemIdCaller;
    InventBatchId                       batchIdCaller;
    FormObjectSet                       callerDatasource;

    InventTrackingBatchSummaryFilter    traceFilter;

    //locally cached potency values
    ItemId                              selectedItemId;
    InventBatchId                       selectedInventBatchId;
    [FormObservable]
    PDSBaseAttributeId                  selectedBaseAttributeId;
    [FormObservable]
    PDSBaseValue                        selectedBaseTargetValue;
    [FormObservable]
    PDSBaseAttributeValues              selectedBatchActualAttributeValue;
    [FormObservable]
    PDSNormalizedQty                    selectedBatchActiveQuantityOnHand;
    [FormObservable]
    PDSAvailableQty                     selectedBatchAvailableQuantityOnHand;

}

void close()
{
    element.returnId2JumpRef();

    super();
}

public void doRefresh()
{
    element.doResearch();
}

void hideQualityManagement()
{
    GroupInquiries.visible(false); // currently only quality related queries)
    inventNonConformanceTable.visible(false);
    inventQualityOrderTable.visible(false);
    QualityManagement.visible(false);
}

void init()
{
    traceFilter = element.args().parmObject() as InventTrackingBatchSummaryFilter;
    if (traceFilter)
    {
        traceFilter.parmFormRun(this);
    }

    super();

    if (traceFilter)
    {
        traceFilter.formInitPost();
    }
    else if (fieldName2id(element.args().dataset(),fieldStr(InventTable,ItemId)))
    {
        inventBatch_ds.object(fieldNum(InventBatch, ItemId)).visible(false);
    }

    if (!InventParameters::find().UseQualityManagement)
    {
        element.hideQualityManagement();
    }

    WHSWarehouseTransactions.visible(WHSWarehouseTransactionsButtonFlight::instance().isEnabled());
}

void returnId2JumpRef()
{
    InventDim       inventDim;
    FormRun         callerForm;
    FormControl     selectedControl;

    if (!return2JumpRef || !callerDatasource || !element.args() || !element.args().caller())
    {
        return;
    }

    if (!(callerDatasource.cursor().TableId == tableNum(InventDim)))
    {
        return;
    }

    callerForm          = element.args().caller();
    selectedControl     = callerForm.selectedControl();

    if (!selectedControl.allowEdit() || !selectedControl.enabled())
    {
        return;
    }

    inventDim = callerDatasource.cursor() as InventDim;
    inventDim.InventBatchId = inventBatch.InventBatchId;
    if (callerDatasource)
    {
        callerDatasource.refresh();
    }
}

void run()
{
    #Task
    InventBatchJournalResult inventBatchJournalResult;

    itemIdCaller = InventDim::findItemIdFromJumpRef(element.args());

    if (!itemIdCaller && element.args().record())
    {
        switch (element.args().dataset())
        {
            case tableNum(PurchLine) :
                itemIdCaller = element.args().record().(fieldNum(PurchLine,ItemId));
                batchIdCaller = InventDim::find(
                        element.args()
                        .record().(fieldNum(PurchLine,InventDimId)))
                    .InventBatchId;
                break;
            case tableNum(PurchParmLine) :
                itemIdCaller = element.args().record().(fieldNum(PurchParmLine,ItemId));
                batchIdCaller = InventDim::find(
                        element.args()
                        .record().(fieldNum(PurchParmLine,InventDimId)))
                    .InventBatchId;
                break;
            case tableNum(ProdTable) :
                itemIdCaller = element.args().record().(fieldNum(ProdTable,ItemId));
                batchIdCaller = InventDim::find(
                        element.args()
                        .record().(fieldNum(ProdTable,InventDimId)))
                    .InventBatchId;
                break;
            case tableNum(InventTransferLine) :
                itemIdCaller = element.args().record().(fieldNum(InventTransferLine,ItemId));
                batchIdCaller = InventDim::find(
                        element.args()
                        .record().(fieldNum(InventTransferLine,InventDimId)))
                    .InventBatchId;
                break;
            case tableNum(InventBatchJournalResult) :
                inventBatchJournalResult = element.args().record();
                itemIdCaller  = inventBatchJournalResult.inventBatchJournal().ItemId;
                batchIdCaller = inventBatchJournalResult.inventDim().inventBatchId;
                break;
            case tableNum(TmpPdsBatchSelect) :
                TmpPdsBatchSelect tmpPdsBatchSelect = element.args().record();
                itemIdCaller  = tmpPdsBatchSelect.ItemId;
                batchIdCaller = tmpPdsBatchSelect.InventBatchId;
                break;
        }
    }

    super();

    if (!batchIdCaller && !traceFilter)
    {
        element.task(#taskSwitchToGridView);
    }
}

public void updateControlsState()
{
    InventTable inventTable;
    boolean isStocked;
    boolean isShelfLife;
    boolean isPotency;

    if (#PdsEnabled)
    {
        pdsBatchRestrictInfo.enabled(inventBatch.pdsDispositionStatus() == PdsStatus::Unavailable);

        inventTable = InventTable::find(inventBatch.ItemId);
        isStocked = inventTable.isStocked();
        pdsBatchAttributes.enabled(isStocked);
        pdsResetDispositionStatus.enabled(isStocked);
        pdsResetShelfDates.enabled(isStocked);
        pdsResetVendorBatchInfo.enabled(isStocked);

        inventBatch_ds.object(fieldNum(InventBatch, ExpDate)).allowEdit(!inventTable.isShelfLifeItem());
        inventBatch_ds.object(fieldNum(InventBatch, PdsVendExpiryDate)).allowEdit(#pdsVendBatchEnabled);
        inventBatch_ds.object(fieldNum(InventBatch, PdsUseVendBatchExp)).allowEdit(#pdsVendBatchEnabled);

        isShelfLife = InventTable::find(inventBatch.ItemId).isShelfLifeItem();
        inventBatch_ds.object(fieldNum(InventBatch, ProdDate)).mandatory(isShelfLife);

        isPotency = (inventTable.pdsBaseAttributeId != '');
        PDSPotencyGeneral.enabled(isPotency);
        PDSPotencyGeneral.visible(isPotency);
        PDSPotencyGeneral.skip(!isPotency);
    }
}

/// <summary>
///     Updates the cached potency information.
/// </summary>
public void updatePotencyDisplayValues()
{
    if ((selectedItemId == inventBatch.ItemId) && (selectedInventBatchId == inventBatch.InventBatchId))
    {
        // the record didn't change, nothing to update
        return;
    }

    selectedItemId          = inventBatch.ItemId;
    selectedInventBatchId   = inventBatch.InventBatchId;
    selectedBaseAttributeId = InventTable::find(inventBatch.ItemId).pdsBaseAttributeId;

    //  check for potency item is done locally to avoid a roundtrip potentially for no reason
    if (selectedBaseAttributeId)
    {
        [selectedBatchActualAttributeValue, selectedBaseTargetValue, selectedBatchAvailableQuantityOnHand, selectedBatchActiveQuantityOnHand] = inventBatch.getPotencyBatchManagementDisplayValues();
    }
    else
    {
        selectedBatchActualAttributeValue    = '';
        selectedBaseTargetValue              = '';
        selectedBatchAvailableQuantityOnHand = 0;
        selectedBatchActiveQuantityOnHand    = 0;
    }
}

public void init()
{
    super();
    inventBatch_ds.cacheAddMethod(tableMethodStr(InventBatch, qualityOrderStatusDisplay));

    if (traceFilter)
    {
        traceFilter.parmMainDataSource(inventBatch_ds);
        traceFilter.parmFilterDataSource(inventItemDimTmpFilter_ds);
        traceFilter.dataSourceInitPost();
    }
}

public void initValue()
{
    inventBatch.initFromCaller(element.args());
    super();

    if (inventBatch.ItemId && InventTable::find(inventBatch.ItemId).isShelfLifeItem())
    {
        inventBatch.initializeProductionDate();
    }
}

public display PdsBatchDispositionStatus pdsDispositionStatus(InventBatch _inventBatch)
{
    PdsBatchDispositionStatus   status = PdsStatus::NotApplicable;

    if (hasFieldAccess(tableNum(InventBatch),fieldNum(InventBatch,PdsDispositionCode),AccessType::View))
    {
        if (_inventBatch.PdsDispositionCode)
        {
            status = PdsDispositionMaster::find(_inventBatch.PdsDispositionCode).Status;
        }
    }

    return status;
}

void write()
{
    if (jumpRefMode && !inventBatch.RecId)
    {
        return2JumpRef = true;
    }

    super();
}

public void executeQuery()
{
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;
    QueryBuildRange         qbrBatchId;

    if (traceFilter)
    {
        traceFilter.applyFilter();
    }
    else if (itemIdCaller)
    {
        queryBuildDataSource = inventBatch_ds.query().dataSourceTable(tableNum(InventBatch));
        queryBuildRange = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(InventBatch, ItemId));
        queryBuildRange.value(queryValue(itemIdCaller));

        if (batchIdCaller)
        {
            qbrBatchId = SysQuery::findOrCreateRange(
                            queryBuildDataSource,
                            fieldNum(InventBatch,InventBatchId));
            qbrBatchId.value(queryValue(batchIdCaller));
        }
    }

    super();

    //
    // re-fetch the potency values
    // as background data may have changed
    //
    selectedItemId = '';
}

public int active()
{
    int ret = super();

    element.updateControlsState();

    return ret;
}

