public void modified()
{
    super();

    if (    inventBatch.ProdDate == dateNull()
        &&  inventBatch.ItemId
        &&  InventTable::find(inventBatch.ItemId).isShelfLifeItem())
    {
        inventBatch.initializeProductionDate();
    }

    element.updateControlsState();
}

void clicked()
{
    super();

    InventBatch::pdsCheckDisposition(inventBatch.InventBatchId,
                                     inventBatch.ItemId,
                                     PdsDispDisplayType::Both);
}
