[Form]
public class InventBatchIdLookup extends FormRun
{
    InventDimCtrl_Frm_Lookup    inventDimFormSetup;

    // Packed
    boolean                     showAvail;
    boolean                     linkDim;    // Must link on all dimensions in caller form

    QueryBuildRange             rangeRecId;

    FormStringControl           callerControl;
    FormStringControl           selectedControl;
    FormDataSource              selectedFormDataSource;
    boolean                     filterLookup;

    boolean                     returnMode;

    [FormObservable]
    ItemId                      pdsItemId;
    [FormObservable]
    PDSBaseAttributeId          baseAttribute;
    ProdBOM                     callerProdBOM;

    InventAvailabilityFormCache inventAvailabilityFormCache;
    Map                         tab2ComboBoxItemMap;

    const int TabPageReferenceIndex = 3;
    const int MaxTabIndex = 2;

    #define.CurrentVersion(1)
    #localmacro.CurrentList
            ShowAvail,
            LinkDim
    #endmacro

}

void close()
{
    xSysLastValue::saveLast(this);
    super();
}

void init()
{
    super();

    this.initReturnMode();

    callerControl   = SysTableLookup::getCallerStringControl(element.args());

    element.updateDesign(InventDimFormDesignUpdate::Init);

    inventDimFormSetup.initInventDimQueryDatasource(InventDim_DS);
    inventDimFormSetup.initInventSumQueryDatasource(InventDim_DS);

    xSysLastValue::getLast(this);

    ctrlShowAvail.value(showAvail);
    element.setControlsVisibility();

    if (inventDimFormSetup.hasCallerInventDimParm())
    {
        ctrlLinkDim.value(linkDim);
    }
    else
    {
        linkDim = false;
        ctrlLinkDim.visible(false);
    }

    inventAvailabilityFormCache = InventAvailabilityFormCache::construct();

    if (#PDSPotencyEnabled)
    {
        if (inventDimFormSetup.hasCallerItemId())
        {
            pdsItemId = inventDimFormSetup.callerItemId();
            baseAttribute = InventTable::find(pdsItemId).PDSBaseAttributeId;
        }

        if (element.args().dataset() == tableNum(ProdBOM))
        {
            callerProdBOM = element.args().record();
            PDSNormalizedQty.visible(true);
        }
    }
}

void initParmDefault()
{
    showAvail = true;
}

private void initReturnMode()
{
    if (element.args() && element.args().dataset() == tableNum(InventDim))
    {
        SalesLine returnLine = InventLookupReturn::initSalesReturnMode(element.args().record());

        if (returnLine.InventTransIdReturn)
        {
            returnMode = true;
            tabPageBatch.visible(false);
            tabPageOnHand.visible(false);

            tabPageReference.visible(true);

            tab.showTabs(false);

            OptionsGroup.visible(false);

            inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).clearDynalinks();
            inventTrans_ds.query().dataSourceTable(tableNum(InventTransOrigin)).addRange(fieldNum(InventTransOrigin,InventTransId)).value(queryValue(returnLine.InventTransIdReturn));
            inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).addRange(fieldNum(InventTrans, InvoiceId)).value(SysQuery::valueNotEmptyString());
        }
    }
}

InventDimCtrl_Frm inventDimSetupObject()
{
    return inventDimFormSetup;
}

