public container pack()
{
    return [#CurrentVersion,#CurrentList,tab.tab()];
}

void run()
{
    tab2ComboBoxItemMap = SysLookup::tab2ComboBox(tab, switchView);
    // Execute query on the datasource corresponding to controls in the tab and set visibility of other controls
    switchView.initTab();

    filterLookup    = SysTableLookup::filterLookupPreRun_2DS(callerControl,
                                                             selectedControl,
                                                             inventBatch_ds,
                                                             inventDim_ds);
    this.setShowAvail();

    super();

    SysTableLookup::filterLookupPostRun(filterLookup,
                                        callerControl.text(),
                                        selectedControl,
                                        selectedFormDataSource);

    if (returnMode)
    {
        tab.tab(TabPageReferenceIndex);
        tab.tabChanged(0, TabPageReferenceIndex);
    }
}

private void setControlsVisibility()
{
    ctrlShowAvail.visible(tabPageOnHand.isActivePage());
    ctrlLinkDim.visible(tabPageOnHand.isActivePage());
}

void setSelectMode()
{
    void setSelectedControlAndDataSource(FormStringControl  _selectedControl,
                                         FormDataSource     _selectedFormDataSource)
    {
        selectedControl         = _selectedControl;
        selectedFormDataSource  = _selectedFormDataSource;
    }

    if (inventDimFormSetup.hasCallerItemId())
    {
        if (tabPageOnHand.isActivePAge())
        {
            this.selectMode(inventDim_inventBatchId);
            inventDim_inventBatchId.setFocus();
            setSelectedControlAndDataSource(inventDim_inventBatchId, inventDim_ds);
        }
        else if (tabPageReference.isActivePage())
        {
            this.selectMode(InventDimTrans_inventBatchId);
            InventDimTrans_inventBatchId.setFocus();
            setSelectedControlAndDataSource(InventDimTrans_inventBatchId, InventDimTrans_ds);
        }
        else
        {
            this.selectMode(ctrlInventBatch_inventBatchId);
            ctrlInventBatch_inventBatchId.setFocus();
            setSelectedControlAndDataSource(ctrlInventBatch_inventBatchId, inventBatch_ds);
        }
    }
    else
    {
        this.selectMode(ctrlInventBatch_inventBatchId);
        setSelectedControlAndDataSource(ctrlInventBatch_inventBatchId, inventBatch_ds);
    }
}

void setShowAvail()
{
    ctrlInventSum_AvailOrdered.visible(ctrlShowAvail.value());
    ctrlInventSum_AvailPhysical.visible(ctrlShowAvail.value());
    ctrlphysicalInvent.visible(!ctrlShowAvail.value());
}

public boolean unpack(container _packedClass)
{
    Integer     tmpVersion     = conPeek(_packedClass,1);
    Integer     tabNo;

    switch (tmpVersion)
    {
        case #CurrentVersion :
            [tmpVersion,#CurrentList,tabNo] = _packedClass;
            break;
        default :
            return false;
    }

    tab.tab((tabNo > MaxTabIndex) ? MaxTabIndex: tabNo);
    element.setControlsVisibility();
    return true;
}

void updateDesign(InventDimFormDesignUpdate   _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (! inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Lookup::newFromFormSortField(element,fieldNum(InventDim,InventBatchId));
            }

            if (! inventDimFormSetup.hasCallerItemId())
            {
                tabPageOnHand.visible(false);
                tab.showTabs(false);
                OptionsGroup.visible(false);
            }
            else
            {
                inventDimFormSetup.formSetControls(true,true);
            }
            break;
        case InventDimFormDesignUpdate::LinkActive:
            inventDimFormSetup.formActiveSetup();
            inventDimFormSetup.formSetControls(true,true);
            break;
        default:
            throw error(strFmt("@SYS54195",funcName()));
    }
}

public display PdsBatchAttribValue pdsBaseAttributeValue(InventBatch _inventBatch)
{
    PdsBatchAttribId  baseAttributeId;

    baseAttributeId = this.pdsBaseAttribute(_inventBatch);

    if (!baseAttributeId)
    {
        return '';
    }

    return PdsBatchAttributes::find(_inventBatch.ItemId, _inventBatch.InventBatchId, baseAttributeId).PdsBatchAttribValue;
}

public display PdsBatchAttribId pdsBaseAttribute(InventBatch _inventBatch)
{
    if (baseAttribute)
    {
        return baseAttribute;
    }

    return InventTable::find(_inventBatch.ItemId).PDSBaseAttributeId;
}

void init()
{
    Query                   q;
    QueryBuildDataSource    qbds;

    super();

    q = new Query();
    qbds = q.addDataSource(tableNum(InventBatch));
    qbds.name(inventBatch_ds.name());
    qbds.addSortField(fieldNum(InventBatch,InventBatchId));

    this.query(q);
}

void executeQuery()
{   
    QueryBuildDataSource    qbds = this.query().dataSourceNo(1);
    FieldId                 fieldId;
    ItemId                  itemId;

    if (#PdsShelfEnabled)
    {
        itemId = inventDimFormSetup.callerItemId();
    }

    fieldId = inventDimFormSetup.callerItemFieldId();
    if (fieldId)
    {
        if (! qbds.dynalinkCount())
        {
            qbds.addDynalink(
                fieldNum(InventBatch,ItemId),
                inventDimFormSetup.callerItemIdFormDatasource().cursor(),
                fieldId);
        }
    }
    else
    {
        if (inventDimFormSetup.hasCallerItemId())
        {
            qbds.addRange(fieldNum(InventBatch,ItemId)).value(queryValue(inventDimFormSetup.callerItemId()));
        }
    }

    if (#PdsShelfEnabled)
    {
        if (inventDimFormSetup
            && itemId
            && InventTable::find(itemId).pdsCheck())
        {                                          
            qbds.sortClear();
            if (InventTable::find(itemId).modelGroup().PdsPickCriteria == PdsPickCriteria::ExpiryDate)
            {
                qbds.addSortField(fieldNum(InventBatch,ExpDate));
            }
            else
            {
                qbds.addSortField(fieldNum(InventBatch,PdsBestBeforeDate));
            }

            qbds.addSortField(fieldNum(InventBatch,InventBatchId));
        }
    }

    super();
}

public display InventQtyAvailOrdered availOrdered(InventDim _inventDim)
{
    InventSum_DS.observe();
    return inventAvailabilityFormCache.availabilityFromDimJoinedWithSum(_inventDim, InventSum_DS).availTotal();
}

public display InventQtyAvailPhysical availPhysical(InventDim _inventDim)
{
    InventSum_DS.observe();
    return inventAvailabilityFormCache.availabilityFromDimJoinedWithSum(_inventDim, InventSum_DS).availPhysical();
}

public display PDSNormalizedQty pdsNormalizedQty(InventDim _inventDim)
{
    InventQtyAvailPhysical availPhysical;
    Qty normalizedQty;

    InventSum_DS.observe();

    //base attribute is set in init method for the form
    if (!baseAttribute)
    {
        return 0;
    }

    availPhysical = inventAvailabilityFormCache.availabilityFromDimJoinedWithSum(_inventDim, inventSum_ds).availPhysical();
    if (availPhysical > 0)
    {
        normalizedQty = PDSBalanceIngredient::normalizeActiveQty(pdsItemId, _inventDim.inventBatchId, availPhysical);
    }

    return normalizedQty;
}

void init()
{
    Query                   q;
    QueryBuildDataSource    qbds;

    super();

    q = new Query();
    qbds = q.addDataSource(tableNum(InventDim));
    qbds.name(inventDim_ds.name());
    this.query(q);
}

void executeQuery()
{
    if (inventDimFormSetup.hasCallerItemId())
    {
        inventAvailabilityFormCache.refreshCache();

        inventDimFormSetup.executeInventDimQueryDatasource(this,
                                                           linkDim,
                                                           showAvail
                                                          );

        element.updateDesign(InventDimFormDesignUpdate::LinkActive);

        super();
    }
}

