void executeQuery()
{
    #DEFINE.QueryNoLiterals(2)

    QueryBuildDataSource    qbsTrans;
    QueryBuildDataSource    qbsDim;

    if (returnMode)
    {
        super();
        return;
    }

    this.query().literals(#QueryNoLiterals);

    qbsTrans = this.query().dataSourceTable(tableNum(InventTrans));
    qbsDim   = this.query().dataSourceTable(tableNum(InventDim));

    qbsTrans.sortClear();
    qbsDim.sortClear();

    if (inventDim.InventBatchId)
    {
        InventDim::queryAddRangeFromDSSort(inventTrans_ds.query().dataSourceTable(tableNum(InventDim)),inventDim_DS);

        rangeRecId.value(SysQuery::valueUnlimited());

        qbsTrans.addSortIndex(indexNum(InventTrans,DimIdIdx));
        qbsTrans.indexIsHint(true);

        qbsDim.addSortIndex(indexNum(InventDim,BatchIdIdx));
        qbsDim.indexIsHint(true);
    }
    else
    {
        rangeRecId.value(queryValue(-1));

        this.query().literals(#QueryNoLiterals);

        qbsTrans.addSortIndex(indexNum(InventTrans,recId));
        qbsTrans.indexIsHint(true);

        qbsDim.addSortIndex(indexNum(InventDim,DimIdIdx));
        qbsDim.indexIsHint(true);
    }

    super();
}

void init()
{
    super();

    rangeRecId = this.query().dataSourceTable(tableNum(InventTrans),1).addRange(fieldNum(InventTrans,RecId));
}

public boolean modified()
{
    boolean ret;

    ret = super();

    this.initTab();

    return ret;
}

public void initTab()
{
    tab.tabChanged(tab.tabValue(), tab2ComboBoxItemMap.lookup(this.selection()));
    element.setControlsVisibility();
}

public void clicked()
{
    super();

    showAvail = ! showAvail;

    inventDim_ds.executeQuery();

    element.setShowAvail();
}

public void clicked()
{
    super();

    linkDim = ! linkDim;

    inventDim_ds.executeQuery();
}

public void tabChanged(int _fromTab, int _toTab)
{
    super(_fromTab, _toTab);

    element.setSelectMode();

    if (_toTab == TabPageReferenceIndex)
    {
        element.selectMode(InventDimTrans_inventBatchId);
        inventDimTrans_inventBatchId.setFocus();
        inventTrans_ds.executeQuery();
    }
    else
    {
        if (filterLookup)
        {
            selectedFormDataSource.research();
        }

        SysTableLookup::lookupTabChanged(callerControl,
                                        selectedControl,
                                        selectedFormDataSource);
    }
}
