[Form]
#ISOCountryRegionCodes
public final class InventClosing extends FormRun
{
    #MACROLIB.resAppl

    QueryBuildRange     criteriaActiveAll;
    QueryBuildRange     criteriaRegType;
    [FormObservable]
    ImageListAppl       imageListAppl;
    // <GEERU>

    InventTransCurrency_RU  inventTransCurrency;
    // </GEERU>

    Map                 hasLogMap;

}

public void init()
{
    // <GEERU>

    Args args = this.args();

    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        if (!args || args.parmEnumType() != enumnum(InventTransCurrency_RU))
        {
            throw error(Error::wrongUseOfFunction(funcname()));
        }
        inventTransCurrency = args.parmEnum();
    }
    // </GEERU>
    super();

    inventCostClosingPreClose.visible(inventTransCurrency == InventTransCurrency_RU::PrimaryCur);
    imageListAppl = new ImageListAppl_InventSettlement();
    windowEnded.imageList(imageListAppl.imageList());

    Cancellation.text("@SCM:InventClosingCancellationName");
    InventClosing_Cancellation.label("@SCM:InventClosingCancellationName");
    Identification_Cancellation.label("@SCM:InventClosingCancellationName");
    Setup_CancelRecalculation.label("@SCM:InventClosingCancellationGeneralTabSetUp");
}

/// <summary>
/// Determines if the <c>InventClosing</c> record has a log associated with it.
/// </summary>
/// <param name="_inventClosing">
/// The <c>InventClosing</c> buffer.
/// </param>
/// <returns>
/// true, if the <c>InventClosing</c> record has a log associated with it; otherwise, false.
/// </returns>
/// <remarks>
/// The result is cached in a map, so the method can be used in both form logic and as a display method.
/// </remarks>
[SysClientCacheDataMethodAttribute(true)]
display boolean closingHasLog(InventClosing _inventClosing)
{
    if (!hasLogMap.exists(_inventClosing.RecId))
    {
        if (_inventClosing.InventCostStatus == InventCostStatus::Ended)
        {
            hasLogMap.insert(_inventClosing.RecId, _inventClosing.hasLog());
        }
        else
        {
            return _inventClosing.hasLog();
        }
    }
    return hasLogMap.lookup(_inventClosing.RecId);
}

private boolean enableCancelattion()
{
    return (inventClosing.Active &&
            inventClosing.InventCostStatus == InventCostStatus::Ended &&
            InventClosing.AdjustmentType != InventAdjustmentType::Revaluation &&
            InventClosing.AdjustmentType != InventAdjustmentType::PreClose);
}

public int active()
{
    int ret;

    ret = super();

    if (!inventClosing.Active ||
        inventClosing.Cancellation ||
        inventClosing.AdjustmentType == InventAdjustmentType::Recalculation ||
        inventClosing.AdjustmentType == InventAdjustmentType::PreClose ||
        inventClosing.InventCostStatus != InventCostStatus::Ended)
    {
        adjOnHand.enabled(false);
    }
    else
    {
        adjOnHand.enabled(true);
    }

    logButton.enabled(this.closingHasLog(inventClosing));
    cancellation.enabled(this.enableCancelattion());

    return ret;
}

display ImageRes endedOk(InventClosing _inventClosing)
{
    if (_inventClosing.InventCostStatus != InventCostStatus::Ended)
    {
        return imageListAppl.image(#Image_NotAtAllOK);
    }

    return imageListAppl.image(#Image_OK);
}

void init()
{
    super();

    hasLogMap = new Map(Types::Int64, Types::Enum);

    criteriaActiveAll       = this.query().dataSourceNo(1).addRange(fieldNum(InventClosing,Active));

    this.query().dataSourceNo(1).addSortField(fieldNum(InventClosing,TransDate),SortOrder::Ascending);
    this.query().dataSourceNo(1).addSortField(fieldNum(InventClosing,AdjustmentType),SortOrder::Descending);
    this.query().dataSourceNo(1).addSortField(fieldNum(InventClosing,Voucher),SortOrder::Ascending);

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        findOrCreateRange_W(this.query().dataSourceNo(1), fieldnum(InventClosing, InventTransCurrency_RU), queryValue(inventTransCurrency)).status(RangeStatus::Locked);
    }
    // </GEERU>
}

void executeQuery()
{
    switch (ctrlActiveAll.selection())
    {
        case InventActiveAll::Active:
            criteriaActiveAll.value(enum2Value(NoYes::Yes));
            break;
        case InventActiveAll::All :
            criteriaActiveAll.value('');
            break;
    }

    super();
}

display container statusIcon(InventClosing _inventClosing)
{
    if (_inventClosing.InventCostStatus != InventCostStatus::Ended)
    {
        if (_inventClosing.StopRunning)
        {
            if (_inventClosing.StopOnError)
            {
                return ImageReference::constructForSymbol(ImageReferenceSymbol::RedX, "@SYS32112").pack(); // Failed
            }
            else
            {
                return ImageReference::constructForSymbol(ImageReferenceSymbol::Pause, "@SYS4007228").pack();  // Paused
            }
        }
        else
        {
            return ImageReference::constructForSymbol(ImageReferenceSymbol::ProgressOuterLoop, "@SYS8577").pack(); // Processing
        }
    }

    return ImageReference::constructForSymbol(ImageReferenceSymbol::Checkmark, "@SYS5294").pack(); // Complete
}

void clicked()
{
    inventCostStop.enabled(! inventClosing.StopRunning && inventClosing.InventCostStatus != InventCostStatus::Ended);
    inventCostHelp.enabled(  inventClosing.StopRunning && inventClosing.InventCostStatus != InventCostStatus::Ended);

    super();
}

void clicked()
{
    super();

    Args args = new Args();
    args.record(inventClosing);
    args.caller(this);

    new MenuFunction(menuItemDisplayStr(InventClosingLog), MenuItemType::Display).run(args);
}

int selectionChange()
{
    int ret;

    ret = super();

    inventClosing_ds.executeQuery();

    return ret;
}
