[Form]
public class InventCostOnhandItem extends FormRun
{
    InventDimCtrl_Frm_CostOnhandItem    inventDimFormSetup;
    ItemId                              currentItemId;
    boolean                             isItemStandardCost;
    boolean                             isFinancialState;

    InventTable                         inventTableCaller;
    InventSum                           inventSumCaller;
    InventDimParm                       inventDimParm;
    InventDimParm                       dimensionDisplayParm;

}

/// <summary>
/// Gets the inventory dimensions currently selected for display.
/// </summary>
/// <returns>An instance of <c>InventDimParm</c> class representing currently selected dimensions.</returns>
public InventDimParm currentInventParameters()
{
    return dimensionDisplayParm;
}

/// <summary>
/// Gets the id of the item for which the cost objects or physical values are displayed.
/// </summary>
/// <returns>The item id for which the cost objects or physical values are displayed.</returns>
public ItemId selectedItemId()
{
    return currentItemId;
}

/// <summary>
/// Gets a flag indicating if the form is displaying cost objects or physical values.
/// </summary>
/// <returns>True if form state is financial; otherwise false.</returns>
public boolean isFinancialState()
{
    return isFinancialState;
}

private void loadDimensionColumns()
{
    if (this.isTableCaller(tableNum(InventTable)))
    {
        inventTableCaller = element.args().record();
        currentItemId = inventTableCaller.ItemId;
        isFinancialState = true;
        InventDimGroupSetup groupSetup = InventDimGroupSetup::newItemId(currentItemId);
        groupSetup.inventDimParmActiveFinancialInventory(inventDimParm);
        dimensionDisplayParm = inventDimParm;
        this.setFormControlState(true, "@SCM:InventCostObjects");
        if (element.args().parm())
        {
            InventDim::queryAddRangeCriteria(InventDim_Q.dataSourceTable(tablenum(InventDim)), InventDim::find(element.args().parm()));
        }
    }
    else if (this.isTableCaller(tableNum(InventSum)))
    {
        InventDim::queryAddRangeFromCallerSort(InventDim_Q.dataSourceTable(tablenum(InventDim)), element.args());
        inventSumCaller =  element.args().record();
        currentItemId = inventSumCaller.ItemId;
        isFinancialState = false;
        InventDimGroupSetup groupSetup = InventDimGroupSetup::newItemId(currentItemId);

        element.lockFinanciallyActiveDimensionRanges(groupSetup);

        groupSetup.inventDimParmActivePhysicalInventory(inventDimParm);
        groupSetup.inventDimParmActiveOnlyPhysicalInventory(dimensionDisplayParm);
        this.setFormControlState(false, "@SCM:PhysicalCostQuantityCaption");
    }
    else
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }
}

private void lockFinanciallyActiveDimensionRanges(InventDimGroupSetup _groupSetup)
{
    _groupSetup.inventDimParmActiveFinancialInventory(inventDimParm);
    ListEnumerator financiallyActiveDimensions = inventDimParm.selectedDimFields().getEnumerator();
    while (financiallyActiveDimensions.moveNext())
    {
        FieldId current = financiallyActiveDimensions.current();
        InventDim_Q.dataSourceTable(tableNum(InventDim)).rangeField(current).status(RangeStatus::Locked);
    }
}

private boolean isTableCaller(TableId _tableId)
{
    return element.args().record() && element.args().record().TableId == _tableId;
}

private void setFormControlState(boolean _isVisible, str _caption)
{
    element.design().caption(_caption);
    StdCostTransactions.visible(_isVisible);
    InventCostOnhandItemAction.visible(_isVisible);
    InventTransItemIdx.visible(_isVisible);
    InventValueTransView.visible(_isVisible);
    CostInventoryStatement.visible(_isVisible);
    InventModelGroupItem inventModelGroupItem = inventTableCaller.inventModelGroupItem();
    if (_isVisible && inventModelGroupItem)
    {
        isItemStandardCost = inventModelGroupItem.modelGroup().InventModel == InventModel::StdCost;
        StdCostTransactions.enabled(isItemStandardCost);
    }
}

private void setCostObjectButtonsState()
{
    InventDimParm inventDimParmLocal;

    InventDimGroupSetup groupSetup = InventDimGroupSetup::newItemId(currentItemId);
    groupSetup.inventDimParmActiveFinancialInventory(inventDimParmLocal);
    int financiallyActiveDimensionsCount = inventDimParmLocal.selectedDimFields().elements();
    int currentlyDisplayedDimensionsCount = inventDimParm.selectedDimFields().elements();

    boolean isCostObjectEnabled = financiallyActiveDimensionsCount == currentlyDisplayedDimensionsCount;

    StdCostTransactions.enabled(isItemStandardCost && isCostObjectEnabled);
    InventTransItemIdx.enabled(isCostObjectEnabled);
    InventCostOnhandItemAction.enabled(isCostObjectEnabled);
    InventValueTransView.enabled(isCostObjectEnabled);
}

InventDimCtrl_Frm_CostOnhandItem inventDimSetupObject()
{
    if (!inventDimFormSetup)
    {
        inventDimFormSetup  = InventDimCtrl_Frm_CostOnhandItem::newFromForm(element);
    }
    return inventDimFormSetup;
}

void rightClickModifyQuery()
{
    element.inventDimSetupObject().parmDimParmVisible(element.inventDimSetupObject().parmDimParmVisibleGrid());
    element.inventDimSetupObject().rightClickModifyQueryGroupBy(inventSum_DS);  // Preserve ranges

    inventSum_DS.executeQuery();
}

void updateDesign()
{
    element.inventDimSetupObject().initFromCaller();
    element.inventDimSetupObject().parmDimParmVisibleGrid(inventDimParm);
    element.inventDimSetupObject().formActiveSetup();
    element.inventDimSetupObject().formSetControls(true, true);
}

