/// Initializes the form.
/// </summary>
public void init()
{
    super();
    element.loadDimensionColumns();
    element.updateDesign();
}

/// <summary>
/// Gets the inventory unit of measure for an item related to the given <c>InventSum</c> record.
/// </summary>
/// <param name = "_inventSum">Record for which to determine the inventory unit.</param>
/// <returns>The inventory unit of measure.</returns>
public display UnitOfMeasureSymbol getInventoryUnit(InventSum _inventSum)
{
    return InventTableModule::find(_inventSum.ItemId, ModuleInventPurchSales::Invent).UnitId;
}

void linkActive()
{
    element.loadDimensionColumns();
    element.updateDesign();
    super();
}

void init()
{
    super();

    element.inventDimSetupObject().initQueryInventSum(inventSum_DS);
}

void executeQuery()
{
    element.inventDimSetupObject().modifyQuery(inventSum_DS, inventDim_DS);
    element.setCostObjectButtonsState();

    super();
}

int active()
{
    int ret;

    if (#PdsEnabled)
    {
        inventDim.checkInvalidFieldAccess(false);
    }

    ret = super();

    inventDim_ds.active();

    return ret;
}

/// <summary>
/// Gets the total quantity for a given <c>InventSum</c> record.
/// </summary>
/// <param name = "_inventSum">Record for which to calculate total quantity.</param>
/// <returns>Total quantity.</returns>
public display Qty totalQuantity(InventSum _inventSum)
{
    return _inventSum.PostedQty - abs(_inventSum.Deducted) + _inventSum.Received;
}

/// <summary>
/// Gets the total value for a given <c>InventSum</c> record.
/// </summary>
/// <remarks>
/// In case of aggregation at the physically active dimension level it returns a proportional value
/// based on average cost object value multiplied by total quantity.
/// </remarks>
/// <param name = "_inventSum">Record for which to calculate total value.</param>
/// <returns>Total cost value for financial dimensions;proportional cost value for physical dimensions.</returns>
public display InventTotalValue totalValue(InventSum _inventSum)
{
    InventTotalValue totalCostValue;
    if (isFinancialState)
    {
        totalCostValue = this.costValuePhysicalFinancial(_inventSum);
    }
    else
    {
        totalCostValue = this.totalQuantity(_inventSum) * this.averageCostPriceUnit(_inventSum);
    }
    return totalCostValue;
}

private InventTotalValue costValuePhysicalFinancial(InventSum _inventSum)
{
    return _inventSum.PostedValue + _inventSum.PhysicalValue;
}

/// <summary>
/// Gets the average cost price per unit for a given <c>InventSum</c> record.
/// </summary>
/// <param name = "_inventSum">Record for which to calculate average cost price per unit.</param>
/// <returns>Average cost price per unit.</returns>
public display InventAverageCostPrice averageCostPriceUnit(InventSum _inventSum)
{
    InventSum inventSumLocal = _inventSum;
    if (!isFinancialState)
    {
        inventSumLocal = inventSumCaller;
    }
    var totalQty = this.totalQuantity(inventSumLocal);
    return totalQty == 0 ? 0 : abs(this.costValuePhysicalFinancial(inventSumLocal) / totalQty);
}

void init()
{
    super();

    element.inventDimSetupObject().initQueryInventDim(inventDim_DS);
}
