Method.Source:
[Form]
public class InventCostTrans extends FormRun
{
    InventDimCtrl_Frm_ActiveRightClick  inventDimFormSetup;
    CostSheetPanel_InventCostTrans      costSheetPanel;
    boolean                             deleteCostVariances;

}

void init()
{
    costSheetPanel = CostSheetPanel_InventCostTrans::newForm(element, element.controlId(formControlStr(InventCostTrans, OverviewVariance)));
    super();

    costSheetPanel.init();

    element.updateDesign(InventDimFormDesignUpdate::Init);
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    InventDimParm   inventDimParm;

    switch (mode)
    {
        case InventDimFormDesignUpdate::Init          :
            InventDimSetupGrid::initInventDimParmInventTrans(inventDimParm);

            if (!inventDimFormSetup)
                inventDimFormSetup  = InventDimCtrl_Frm_ActiveRightClick::newFromForm(element);
            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm);
            inventDimFormSetup.formSetControls(true);
    }
}

public void linkActive()
{
    LeanProductionFlowCosting   productionFLowCosting;
    LeanCosting                 costing;
    ProdCalcTrans               prodCalcTrans;
    ProdTable                   prodTable;

    QueryBuildRange             qbrTransRefId;
    QueryBuildRange             qbrVoucher;
    QueryBuildRange             qbrItemId;
    QueryBuildRange             qbrState;

    if (element.args().dataset() == tableNum(LeanProductionFlowCosting) ||
        element.args().dataset() == tableNum(ProdCalcTrans) ||
        element.args().dataset() == tableNum(InventSum))
    {
        qbrTransRefId = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(InventCostTrans)), fieldNum(InventCostTrans, TransRefId));
        qbrItemId = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(InventCostTrans)), fieldNum(InventCostTrans, ItemId));
        qbrVoucher = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(InventCostTrans)), fieldNum(InventCostTrans, Voucher));
    }

    if (element.args().dataset() == tableNum(LeanProductionFlowCosting))
    {
        productionFLowCosting = element.args().record();
        costing = LeanCosting::find(productionFLowCosting.LeanCosting);

        qbrTransRefId.value(productionFLowCosting.displayProductionFlowName());
        qbrVoucher.value(productionFLowCosting.Voucher);

        if (element.args().parmEnum() == NoYes::Yes)
        {
            qbrItemId.value(SysQuery::valueEmptyString());
        }
        else
        {
            qbrItemId.value(SysQuery::valueUnlimited());
        }
    }

    if (element.args().dataset() == tableNum(ProdCalcTrans))
    {
        prodCalcTrans = element.args().record();

        qbrState = SysQuery::findOrCreateRange(this.query().dataSourceTable(tableNum(InventCostTrans)), fieldNum(InventCostTrans, CostTransState));
        qbrState.value(queryValue(InventCostTransState::Financial));

        if (prodCalcTrans.TransRefType == ProdTransRefType::Production)
        {
            prodTable = ProdTable::find(prodCalcTrans.TransRefId);
            qbrTransRefId.value(prodTable.ProdId);
            qbrItemId.value(prodTable.ItemId);
        }

        if (prodCalcTrans.TransRefType == ProdTransRefType::BackflushCosting)
        {
            productionFLowCosting = LeanProductionFlowCosting::find(str2int64(prodCalcTrans.TransRefId));
            qbrTransRefId.value(productionFLowCosting.displayProductionFlowName());
            qbrItemId.value(SysQuery::valueEmptyString());
            qbrVoucher.value(productionFLowCosting.Voucher);
        }
    }

    if (element.args().dataset() == tableNum(InventSum))
    {
        InventSum inventSum = element.args().record();
        qbrItemId.value(inventSum.ItemId);
    }

    super();
}

public void init()
{
    super();
    this.cacheAddMethod(tableMethodStr(InventCostTrans, costAmount));
    this.cacheAddMethod(tableMethodStr(InventCostTrans, varianceAmount));
}

public int active()
{
    int ret;

    ret = super();
    var currentQuery = this.queryRun();

    if (deleteCostVariances)
    {
        costSheetPanel.parmInventCostTrans(null);
        costSheetPanel.fill();
    }
    else
    {
        costSheetPanel.parmInventCostTrans(inventCostTrans);
        costSheetPanel.fill();
    }
    return ret;
}

public void executeQuery()
{
    super();
    if (SysQuery::queryHasFilters(this.queryRun().query()))
    {
        if (SysQuery::countTotal(this.queryRun()) == 0)
        {
            deleteCostVariances = true;
            this.active();
        }
        deleteCostVariances = false;
    }
}

public void jumpRef()
{
    InventTransOrigin inventTransOrigin = InventTrans::findTransId(inventCostTrans.InventTransId).inventTransOrigin();
    if (!inventTransOrigin.jumpRefReferenceId())
    {
        super();
    }
}
