[Form]
public class InventCountingReasonCodePolicyAssigner extends FormRun
{
    private Args                                   args;
    private InventCountingReasonCodePolicyAssigner inventCountingReasonCodePolicyAssigner;

}

public void init()
{
    args = element.args();

    if (!args)
    {
        throw error(Error::missingParameter(args));
    }

    Common callerRecord = args.record();

    if (!callerRecord)
    {
        throw error(Error::missingRecord(tableId2PName(callerRecord.TableId)));
    }

    super();

    inventCountingReasonCodePolicyAssigner = InventCountingReasonCodePolicyAssigner::newFromBuffer(callerRecord);

    InventCountingReasonCodePolicyRecId.value(inventCountingReasonCodePolicyAssigner.inventCountingReasonCodePolicyRecId());
    InventCountingReasonCodePolicyRecId.update();

}

public void closeOk()
{
    ttsbegin;

    boolean policyAssigned = inventCountingReasonCodePolicyAssigner.assignPolicy(InventCountingReasonCodePolicyRecId.value());

    super();

    ttscommit;

    if (policyAssigned)
    {
        var callerFormClose = args.caller() as InventCountingReasonCodePolicyAssignerIFormClose;

        if (callerFormClose)
        {
            callerFormClose.updateCallerDatasources();
        }

    }
}

public void jumpRef()
{
    // Call to super is commented out because we need to implement our own jumpRef logic.
    // super()

    Args menuFunctionArgs     = new Args();
    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(InventCountingReasonCodePolicy), MenuItemType::Display);

    menuFunctionArgs.lookupRecord(InventCountingReasonCodePolicy::find(this.value()));
    menuFunction.run(menuFunctionArgs);
}
