[Form]
public class InventGTDlIdLookup_RU extends FormRun
{
    InventDimCtrl_Frm_Lookup    inventDimFormSetup;

    boolean                     canSelect;
    boolean                     mustKeepFormOpened;

    boolean                     showAvail;
    boolean                     linkDim;
    boolean                     mustSaveLast;

    boolean                     exeQueryItem;
    boolean                     exeQuerySerial;

    QueryBuildRange             rangeRecId;
    boolean                     returnMode;

    InventAvailabilityFormCache inventAvailabilityFormCache;

    const int gtdPage = 1;
    const int itemPage = 2;
    const int refPage = 3;

    const int currentVersion = 3;

}

void close()
{
    if (canSelect)
    {
        if (mustSaveLast)
        {
            xSysLastValue::saveLast(this);
        }

        super();
    }
}

void closeSelect(str _p1)
{
    if (canSelect)
    {
        super(_p1);
    }
    else
    {
        // If canSelect is set to false, than the form hasn't been closed; super() has not been executed
        // If mustKeepFormOpened set to true, then the form has not been closed intentionally,
        // due to user interaction (check/uncheck action on checkbox controls)
        // The canSelect should be switched to true, so the form will be closed next time
        // in the appropriate way; the mustKeepFormOpened should be switched to false as well.
        if (mustKeepFormOpened)
        {
            mustKeepFormOpened    = false;
            canSelect             = true;
        }
    }
}

void init()
{
    super();

    this.initReturnMode();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    inventDimFormSetup.initInventDimQueryDatasource(inventDimItem_ds);
    inventDimFormSetup.initInventSumQueryDatasource(inventSumItem_ds);

    xSysLastValue::getLast(this);
    showAvailBox.value(showAvail);

    if (inventDimFormSetup.hasCallerInventDimParm())
    {
        linkDimBox.value(linkDim);
    }
    else
    {
        linkDim = false;
        linkDimBox.visible(false);
    }

    inventAvailabilityFormCache = InventAvailabilityFormCache::construct();
}

void initParmDefault()
{
    showAvail = true;
}

private void initReturnMode()
{
    SalesLine           returnLine;

    if (element.args() && element.args().dataset() == tableNum(InventDim))
    {
        returnLine = InventLookupReturn::initSalesReturnMode(element.args().record());

        if (returnLine.InventTransIdReturn)
        {
            returnMode = true;
            tabPageGTDId.visible(false);
            tabPageItem.visible(false);
            tab.showTabs(false);

            inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).clearDynalinks();
            inventTrans_ds.query().dataSourceTable(tableNum(InventTransOrigin)).addRange(fieldNum(InventTransOrigin,InventTransId)).value(queryValue(returnLine.InventTransIdReturn));
            inventTrans_ds.query().dataSourceTable(tableNum(InventTrans)).addRange(fieldNum(InventTrans, InvoiceId)).value(SysQuery::valueNotEmptyString());
        }
    }
}

InventDimCtrl_Frm inventDimSetupObject()
{
    return inventDimFormSetup;
}

DataAreaId lastValueDataAreaId()
{
    return curext();
}

private IdentifierName lastValueDesignName()
{
    return this.args().caller().name();
}

private IdentifierName lastValueElementName()
{
    return this.name();
}

private UtilElementType lastValueType()
{
    return UtilElementType::Form;
}

private UserId lastValueUserId()
{
    return curUserId();
}

public container pack()
{
    return [currentVersion, showAvail, linkDim, tab.tab()];
}

void run()
{
    FormStringControl   callerControl   = SysTableLookup::getCallerStringControl(element.args());
    boolean             filterLookup    = false;
    FormStringControl   selectControl;

    if (callerControl.text() && callerControl.hasChanged())
    {
        filterLookup = true;
        inventGTD_ds.autoSearch(false);
        inventDimItem_ds.autoSearch(false);
    }

    this.setShowAvail();

    super();

    this.setSelectMode();

    if (filterLookup)
    {
        inventGTD_ds.research();
        inventDimItem_ds.research();

        selectControl = this.selectedControl();
        selectControl.filter(callerControl.text());
    }

    canSelect           = true;
    mustKeepFormOpened  = false;

    if (returnMode)
    {
        tab.tab(refPage);
        tab.tabChanged(0, refPage);
    }
}

void setSelectMode()
{
    if (inventDimFormSetup.hasCallerItemId())
    {
        switch (tab.tab())
        {
            case gtdPage:
                this.selectMode(inventGTD_InventGTDId);
                inventGTD_InventGTDId.setFocus();
                break;
            case itemPage:
                this.selectMode(inventoryDimensions_InventGtdId_RU);
                inventoryDimensions_InventGtdId_RU.setFocus();
                break;
            case refPage:
                this.selectMode(inventDimTrans_inventGTDId);
                inventDimTrans_inventGTDId.setFocus();
                break;
        }
    }
    else
    {
        this.selectMode(inventGTD_InventGTDId);
    }
}

void setShowAvail()
{
    ctrlInventSumItem_AvailPhysical.visible(showAvailBox.value());
    ctrlInventSumItem_AvailOrdered.visible(showAvailBox.value());
    ctrlPhysicalInvent.visible(! showAvailBox.value());
}

public boolean unpack(container _packedClass)
{
    Integer version = conPeek(_packedClass,1);
    Integer curTab;

    switch (version)
    {
        case currentVersion:
            [version, showAvail, linkDim, curTab] = _packedClass;
            break;
        default :
            return false;
    }

    tab.tab(curTab == refPage ? itemPage : curTab);

    return true;
}

void updateDesign(InventDimFormDesignUpdate   _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (! inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_Lookup::newFromFormSortField(element, fieldNum(InventDim, InventGtdId_RU));
            }

            if (! inventDimFormSetup.hasCallerItemId())
            {
                tabPageItem.visible(false);
                tab.showTabs(false);
            }
            else
            {
                inventDimFormSetup.formSetControls(true, true);
            }
            break;

        case InventDimFormDesignUpdate::LinkActive:
            inventDimFormSetup.formActiveSetup();
            inventDimFormSetup.formSetControls(true, true);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

void init()
{
    Query                   q;
    QueryBuildDataSource    qbds;

    super();

    q = new Query();
    qbds = q.addDataSource(tableNum(InventDim));
    qbds.name(inventDimItem_ds.name());
    this.query(q);
}

void executeQuery()
{
    if (tab.tab() == gtdPage)
    {
        return;
    }

    if (exeQueryItem)
    {
        return;
    }

    if (inventDimFormSetup.hasCallerItemId())
    {
        inventDimFormSetup.executeInventDimQueryDatasource(this, linkDim, showAvail);
        element.updateDesign(InventDimFormDesignUpdate::LinkActive);

        inventAvailabilityFormCache.refreshCache();

        super();
    }

    exeQueryItem = true;
}

/// <summary>
///    Display the physically available inventory.
/// </summary>
/// <param name="_inventDim">
///    The current <c>InventDim</c> table record.
/// </param>
/// <returns>
///    The physically available quantity.
/// </returns>
public display InventQtyAvailPhysical availPhysical(InventDim _inventDim)
{
    return inventAvailabilityFormCache.availabilityFromDimJoinedWithSum(_inventDim, inventSumItem_ds).availPhysical();
}

/// <summary>
///    Display the total available inventory.
/// </summary>
/// <param name="_inventDim">
///    The current <c>InventDim</c> table record.
/// </param>
/// <returns>
///    The total available quantity.
/// </returns>
public display InventQtyAvailOrdered availOrdered(InventDim _inventDim)
{
    return inventAvailabilityFormCache.availabilityFromDimJoinedWithSum(_inventDim, inventSumItem_ds).availTotal();
}

void executeQuery()
{
    if (inventDimFormSetup.hasCallerItemId())
    {
        super();
    }
}

void init()
{
    Query                   q;
    QueryBuildDataSource    qbds;

    super();

    q = new Query();
    qbds = q.addDataSource(tableNum(InventGTD_RU));
    qbds.name(inventGTD_ds.name());
    qbds.addSortField(fieldNum(InventGTD_RU,InventGTDId));

    this.query(q);
}

void executeQuery()
{
    QueryBuildDataSource    qbds = this.query().dataSourceTable(tableNum(InventGTD_RU));
    FieldId                 fieldId;

    if (!exeQuerySerial)
    {
        fieldId = inventDimFormSetup.callerItemFieldId();
        if (fieldId)
        {
            if (! qbds.dynalinkCount())
            {
                qbds.addDynalink(fieldNum(InventGTD_RU,ItemId),
                                 inventDimFormSetup.callerItemIdFormDatasource().cursor(),
                                 fieldId);
            }
        }
        else
        {
            qbds.addRange(fieldNum(InventGTD_RU,ItemId)).value(inventDimFormSetup.callerItemId());
        }
    }

    super();

    exeQuerySerial = true;
}

void executeQuery()
{
    const int queryNoLitterals = 2;
    QueryBuildDataSource    qbsTrans;
    QueryBuildDataSource    qbsDim;

    if (returnMode)
    {
        super();
        return;
    }

    this.query().literals(queryNoLitterals);

    qbsTrans = this.query().dataSourceTable(tableNum(InventTrans));
    qbsDim   = this.query().dataSourceTable(tableNum(InventDim));

    qbsTrans.sortClear();
    qbsDim.sortClear();

    if (inventDimItem.InventGtdId_RU)
    {
        InventDim::queryAddRangeFromDSSort(inventTrans_ds.query().dataSourceTable(tableNum(InventDim)),
                                           inventDimItem_DS);

        rangeRecId.value(SysQuery::valueUnlimited());

        qbsTrans.addSortIndex(indexNum(InventTrans,DimIdIdx));
        qbsTrans.indexIsHint(true);

        qbsDim.addSortIndex(indexNum(InventDim,SerialIdIdx));
        qbsDim.indexIsHint(true);
    }
    else
    {
        rangeRecId.value(queryValue(-1));

        this.query().literals(queryNoLitterals);

        qbsTrans.addSortIndex(indexNum(InventTrans,recId));
        qbsTrans.indexIsHint(true);

        qbsDim.addSortIndex(indexNum(InventDim,DimIdIdx));
        qbsDim.indexIsHint(true);
    }

    super();
}

void init()
{
    super();

    rangeRecId = this.query().dataSourceTable(tableNum(InventTrans), 1).addRange(fieldNum(InventTrans,RecId));

    this.query().forceNestedLoop(true);
}

void tabChanged(int fromTab, int toTab)
{
    mustSaveLast = true;

    super(fromTab, toTab);

    element.setSelectMode();

    switch (tab.tab())
    {
        case gtdPage:
            inventGTD_ds.executeQuery();
            break;
        case itemPage:
            inventDimItem_ds.executeQuery();
            break;
        case refPage:
            inventDimItem_ds.executeQuery();
            inventTrans_ds.executeQuery();
            break;
    }
}

void clicked()
{
    mustSaveLast = true;

    super();

    showAvail = ! showAvail;

    exeQueryItem = false;
    inventDimItem_ds.executeQuery();

    element.setShowAvail();

    mustKeepFormOpened  = true;
    canSelect           = false;
}

void clicked()
{
    mustSaveLast = true;

    super();

    linkDim = ! linkDim;

    exeQueryItem = false;

    inventDimItem_ds.executeQuery();

    mustKeepFormOpened  = true;
    canSelect           = false;
}
