[Form]
public class InventInventoryDataServiceSalesLineSoftReservation extends FormRun
{
    InventOnHandSoftReserveEvent event;

}

private void directlyProcess(boolean _isRevert = false)
{
    if (!InventInventoryDataServiceSoftReservationCompatibleV0V1Flight::instance().isEnabled())
    {
        if (!SoftReserveHistory.RecId)
        {
            return;
        }
    }

    if (salesLine.IsSoftReservedExternally)
    {
        info(strFmt("@SCM:InventoryServiceShouldNotSoftReserveOrRevertOnExteranlSaleslineInfo", any2Str(salesLine.LineNum)));
        return;
    }

    if (event != null && !event.parmIsSoftReservHistoryUpdated())
    {
        InventInventoryDataServiceParameters parameters = InventInventoryDataServiceParameters::find();
        InventInventoryDataServiceSoftReservationHelper::processReservEvent(event, parameters.IsProcessingQueueBackup);
    }
    event = SoftReserveHistory.updateSoftReserveHistoryAndGetSoftReserveEvent(_isRevert);
    if (event != null)
    {
        InventInventoryDataServiceSoftReservationHelper::postSoftReservation(event, SoftReserveHistory);
        SalesLine_ds.research();
    }
}

private void batchProcess(boolean _isRevert = false)
{
    if (!InventInventoryDataServiceSoftReservationCompatibleV0V1Flight::instance().isEnabled())
    {
        if (!SoftReserveHistory.RecId)
        {
            return;
        }
    }

    if (salesLine.IsSoftReservedExternally)
    {
        info(strFmt("@SCM:InventoryServiceShouldNotSoftReserveOrRevertOnExteranlSaleslineInfo", any2Str(salesLine.LineNum)));
        return;
    }

    InventInventoryDataServiceParameters parameter = InventInventoryDataServiceParameters::find();

    if (parameter.RecId)
    {
        SoftReserveHistory.updateSoftReserveHistoryAndInsertSoftReserveQueue(parameter.IsProcessingQueueBackup, _isRevert);
        SalesLine_ds.research();
    }
}

public void init()
{
    if ((!element.args().record()) && (element.args().dataset()))
    {
        throw error("@SYS34359");
    }
    super();
}

public void run()
{
    super();
    if (InventInventoryDataServiceSoftReservationCompatibleV0V1Flight::instance().isEnabled())
    {
        if (SalesLine.IsSoftReservedExternally && SoftReserveHistory && SoftReserveHistory.existsNonZereSoftReservedQuantity())
        {
            throw error(strFmt("@SCM:InventoryServiceShouldNotSoftReserveOrRevertOnExteranlSaleslineOldVersionInfo", any2Str(salesLine.LineNum), any2Str(SoftReserveHistory.SuccessedQty)));
        }

        if (!SoftReserveHistory.RecId)
        {
            if (!InventInventoryDataServiceSoftReservationHelper::hasBeenSoftReserved(SalesLine))
            {
                SoftReserveHistory = InventInventoryDataServiceSoftReserveHistory::newFromSalesLine(SalesLine);
                salesLine_ds.research(true);
            }
            else
            {
                throw error(strFmt("@SCM:InventoryServiceShouldNotSoftReserveOrRevertOnExteranlSaleslineInfo", any2Str(salesLine.LineNum)));
            }
        }
    }
    else
    {
        if (!SoftReserveHistory.RecId)
        {
            warning('@SCM:InventoryServiceSoftReserveHistoryNotExistsWarning');
        }
    }
}

public void close()
{
    super();
    if (event != null && !event.parmIsSoftReservHistoryUpdated())
    {
        InventInventoryDataServiceParameters parameters = InventInventoryDataServiceParameters::find();
        InventInventoryDataServiceSoftReservationHelper::processReservEvent(event, parameters.IsProcessingQueueBackup);
    }
}

/// <summary>
/// Revert all quantities back to Unprocessed
/// </summary>
public void clicked()
{
    super();
    element.directlyProcess(true);
}
