public void init()
{
    this.setLookupRecordFromArgs();

    super();

    this.setStyleOnWarehouseControls();

    // <GIN>
    if (AssetParameters::isDepreciationCompaniesActEnable_IN())
    {
        inventItemGroup_ds.object(fieldnum(InventItemGroup, AssetGroup_IN)).visible(true);
    }
    // </GIN>
}

private void setStyleOnWarehouseControls()
{
    Enumerator enum = element.controlList().getEnumerator();
    while (enum.moveNext())
    {
        FormCheckBoxControl checkBox = enum.current() as FormCheckBoxControl;
        if (checkBox &&
            checkBox.dataSource() == WHSFilterParm_ds.id() &&
            checkBox.style() == CheckBoxStyle::Auto)
        {
            checkBox.style(CheckBoxStyle::Checkbox);
        }
    }
}

    public void jumpRefPosting(InventAccountType inventAccountType)
    {
        LedgerDimensionDefaultAccount   ledgerAccount   = InventPosting::itemLedgerDimensionFromParameter(this.buildItemLedgerDimensionParameter(inventAccountType));

        Args args = new Args();

        if (ledgerAccount)
        {
            args.lookupField(fieldNum(MainAccount, MainAccountId));
            args.lookupValue(LedgerDimensionFacade::getMainAccountFromLedgerDimension(ledgerAccount).MainAccountId);
        }
        args.caller(element);
		new MenuFunction(menuitemDisplayStr(MainAccountDetails), MenuItemType::Display).run(args);
    }

/// <summary>
/// Builds a parameter for searching an inventory posting ledger dimension for an item.
/// </summary>
/// <param name = "_inventAccountType">A inventory account type.</param>
/// <returns>The built parameter instance.</returns>
protected InventPostingItemLedgerDimensionParameters buildItemLedgerDimensionParameter(
    InventAccountType   _inventAccountType)
{
    return InventPostingItemLedgerDimensionParameters::newFromParameters(
        _inventAccountType,
        '',
        inventItemGroup.ItemGroupId);
}

protected void setLookupRecordFromArgs()
{
    InventItemGroupItem    inventItemGroupItem;

    if (this.args().record() is InventItemGroupItem)
    {
        inventItemGroupItem = this.args().record() as InventItemGroupItem;

        this.args().record(null);
        this.args().lookupTable(tableNum(InventItemGroup));
        this.args().lookupField(fieldNum(InventItemGroup, ItemGroupId));
        this.args().lookupValue(inventItemGroupItem.ItemGroupId);
    }
}

public void selectionChanged()
{
    super();

    // Sync corresponding records in table InventItemGroupForm
    if (inventItemGroup.ItemGroupId)
    {
        InventItemGroupForm::syncDataForItemGroupId(inventItemGroup.ItemGroupId);
    }

    inventItemGroupFormSales_ds.executeQuery();
    inventItemGroupFormInvent_ds.executeQuery();
    inventItemGroupFormPurch_ds.executeQuery();
    inventItemGroupFormProd_ds.executeQuery();
}

public void executeQuery()
{
    QueryBuildRange qbr;

    qbr = SysQuery::findOrCreateRange(
            this.query().dataSourceNo(1),
            fieldNum(InventItemGroupForm, LedgerDimensionGroup));
    qbr.value(queryValue(ItemGroupLedgerDimensionGroup::Production));
    qbr = SysQuery::findOrCreateRange(
            this.query().dataSourceNo(1),
            fieldNum(InventItemGroupForm, ItemGroupId));
    qbr.value(queryValue(inventItemGroup.ItemGroupId));

    super();
}

