public boolean validateWrite()
{
    whsFilterParm.ItemGroupId = inventItemGroup.ItemGroupId;

    return super();
}

public void clicked()
{
    var query = new Query();
    QueryBuildDataSource qbdsItem = query.addDataSource(tableNum(InventTable));
    QueryBuildDataSource qbdsItemGroup = qbdsItem.addDataSource(tableNum(InventItemGroupItem));
    qbdsItemGroup.relations(true);
    qbdsItemGroup.addRange(fieldNum(InventItemGroupItem, ItemGroupId)).value(queryValue(InventItemGroup.ItemGroupId));

    var args = new Args();
    var menuFunction = new MenuFunction(menuitemDisplayStr(CostProductDetailsList), MenuItemType::Display);
    args = new Args(menuFunction.object());
    args.caller(element);
    args.menuItemName(menuItemDisplayStr(CostProductDetailsList));
    args.menuItemType(MenuItemType::Display);
    args.initialQuery(InitialQueryParameter::createByQuery(query));
    menuFunction.run(args);
}
