/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    OfficeGeneratedExportMenuItem exportOption = OfficeGeneratedExportMenuItem::construct(
        dataentityviewstr(InventProductDefaultOrderSettingsEntity),
        CustomExportLinesToExcelOptionId);

    exportOption.displayName(strfmt("@SYS76681", "@SYS109813", '', curExt()));
    exportOptions.addEnd(exportOption);

    exportOption = OfficeGeneratedExportMenuItem::construct(
        dataentityviewstr(InventProductSpecificOrderSettingsV3Entity),
        ProductSpecificOrderSettingV3CustomToExcelOptionId);

    exportOption.displayName(strfmt("@SYS76681", "@SCM:Entity_Title_ProductSpecificDefaultOrderSettingsV3", '', curExt()));
    exportOptions.addEnd(exportOption);

    OfficeMenuDataEntityOptions entityContext = OfficeMenuDataEntityOptions::construct(dataentityviewstr(InventProductSpecificOrderSettingsV3Entity));
    entityContext.includeDefault(false);
    entityContext.dataSourceIdInternal(InventItemInventSetup_ds.id());
    _menuOptions.dataEntityOptions().addEnd(entityContext);
}

/// <summary>
/// Gets the set of initial filters to be applied to the template.
/// </summary>
/// <returns>
/// The Map of filters to apply.
/// Keyed by AOT entity name.
/// Values are all Microsoft.Dynamics.Platform.Integration.Office.FilterNode intances.
/// </returns>
/// <remarks>
/// The filters supplied here will override any filters currently in the template.
/// If no filter is supplied for a given entity, any filters currently in the template are retained.
/// </remarks>
public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    Map filtersToApply = new Map(Types::String, Types::Class);
    if (_menuItem.dataEntityName() == dataentityviewstr(InventProductSpecificOrderSettingsV3Entity) && _menuItem.applyRecordFilter())
    {
        ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(InventProductSpecificOrderSettingsV3Entity));

        var filter = filterBuilder.and(
                filterBuilder.areEqual(fieldStr(InventProductSpecificOrderSettingsV3Entity, DataAreaId), curExt()),
                filterBuilder.areEqual(fieldStr(InventProductSpecificOrderSettingsV3Entity, ItemNumber), InventItemPurchSetup.ItemId));

        filtersToApply.insert(dataentityviewstr(InventProductSpecificOrderSettingsV3Entity), filter);

        // A record filter has already been applied
        _menuItem.applyRecordFilter(false);
    }
    return filtersToApply;
}

