/// <summary>
/// Gets the <see cref="ExportToExcelDataEntityContext"/> that specifies what should be exported for the specified menu item.
/// The <c>InventProductDefaultOrderSettingsEntity</c> export is filtered based on company.
/// </summary>
/// <param name="_menuItem">The menu item.</param>
/// <returns>
/// Returns the <see cref="ExportToExcelDataEntityContext"/> that specifies what should be exported for the specified menu item;
/// otherwise, null.
/// </returns>
public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context;

    switch (_menuItem.id())
    {
        case CustomExportLinesToExcelOptionId:
            context = this.addCustomMenuItem(
                dataentityviewstr(InventProductDefaultOrderSettingsEntity),
                tableFieldGroupStr(InventProductDefaultOrderSettingsEntity, AutoReport));
            break;
        case ProductSpecificOrderSettingV2CustomToExcelOptionId:
            context = this.addCustomMenuItem(
                dataentityviewstr(InventProductSpecificOrderSettingsV2Entity),
                tableFieldGroupStr(InventProductSpecificOrderSettingsV2Entity, AutoReport));
            break;
        case ProductSpecificOrderSettingV3CustomToExcelOptionId:
            context = this.addCustomMenuItem(
                dataentityviewstr(InventProductSpecificOrderSettingsV3Entity),
                tableFieldGroupStr(InventProductSpecificOrderSettingsV3Entity, AutoReport));
            break;
    }

    return context;
}

private ExportToExcelDataEntityContext addCustomMenuItem(str _entityName, str _entityFieldGroup)
{
    ExportToExcelDataEntityContext context = ExportToExcelDataEntityContext::construct(_entityName, _entityFieldGroup);

    ListEnumerator enumerator = context.entities().getEnumerator();
    while (enumerator.moveNext())
    {
        ExportToExcelDataEntityInfo entity = enumerator.current();
        ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(_entityName);
        entity.filter(filterBuilder.companyFilter());
        break;
    }

    return context;
}

