    PurchLocationGrpGrid.visible(showStorageDim);
    PurchSiteGrpGrid.visible(showStorageDim);
    PurchOverrideDefaultDimCheckboxGrid.visible(showStorageDim);
    SalesLocationGrpGrid.visible(showStorageDim);
    SalesSiteGrpGrid.visible(showStorageDim);
    SalesOverrideDefaultDimCheckboxGrid.visible(showStorageDim);
    InventLocationGrpGrid.visible(showStorageDim);
    InventSiteGrpGrid.visible(showStorageDim);
    InventOverrideDefaultDimCheckboxGrid.visible(showStorageDim);
}

public void toggleOrderQty()
{
    ButtonOrderQtyToggle.text(strFmt("@SCM:DefaultOrderSettingsShowHideOrderQuantities", showOrderQty ? "@SYS25491" : "@SYS8335"));

    PurchQtyGrpGrid.visible(showOrderQty);
    PurchQtyGrpCWGrid.visible(isCWItem && showOrderQty);
    SalesQtyGrpGrid.visible(showOrderQty);
    SalesQtyGrpCWGrid.visible(isCWItem && showOrderQty);
    InventQtyGrpGrid.visible(showOrderQty);
    InventQtyGrpCWGrid.visible(isCWItem && showOrderQty);

    PurchQtyGrpCW.visible(isCWItem);
    SalesQtyGrpCW.visible(isCWItem);
    InventQtyGrpCW.visible(isCWItem);
}

