void enableInventDimControls()
{
    boolean allowEdit = element.allowEditInventDefaultDim();
    boolean showSite = !element.isSettingsSiteSpecific();

    // Inventory tab page
    InventOverrideDefaultDimCheckbox.visible(!element.isSettingsDefault());
    InventOverrideDefaultDimCheckboxGrid.enabled(!element.isSettingsDefault());

    inventSiteGrp.visible(showSite);
    inventSiteGrpGrid.enabled(showSite);
    inventDimInvent_InventSiteIdInvent.allowEdit(showSite && allowEdit);
    inventDimInvent_InventSiteIdInventGrid.allowEdit(showSite && allowEdit);

    inventDimInvent_InventLocationIdInvent.allowEdit(allowEdit);
    inventDimInvent_InventLocationIdInventGrid.allowEdit(allowEdit);
}

void enableInventMandatoryLocation()
{
    inventItemInventSetup_MandatoryInventLocation.enabled(inventDimInvent.InventLocationId != ''
                                                          && element.allowEditInventDefaultDim());
    InventItemInventSetup_MandatoryInventLocationGrid.enabled(InventItemInventSetup_MandatoryInventLocation.enabled());

    if (!inventDimInvent.InventLocationId)
