void enableInventOverrideControls()
{
    if (!element.isSettingsDefault())
    {
        inventOverrideCheckbox.visible(true);
        inventParametersOverrideCheckbox.visible(false);
        inventDeliveryControlGrp.enableChilds(inventItemInventSetup.Override);

        inventDeliveryControlOverrideCheckboxGrid.enabled(false);
        inventOverrideCheckboxGrid.enabled(true);
        inventDeliveryControlGrpGrid.enableChilds(inventItemInventSetup.Override);
    }
    else
    {
        inventParametersOverrideCheckbox.value(inventItemInventSetup.Override);
        inventOverrideCheckbox.visible(false);

        inventDeliveryControlOverrideCheckboxGrid.value(inventItemInventSetup.Override);
        inventOverrideCheckboxGrid.enabled(false);

        if (!RetailSMB::IsRetailSMBEnabled() && inventTable.ItemType != ItemType::Service)
        {
            inventParametersOverrideCheckbox.visible(true);
            inventDeliveryControlOverrideCheckboxGrid.enabled(true);
        }

        inventDeliveryControlGrp.enableChilds(inventItemInventSetup.Override);
        inventDeliveryControlGrpGrid.enableChilds(inventItemInventSetup.Override);
    }

    boolean enableInventControls = element.isSettingsDefault() || inventItemInventSetup.Override;

    inventStoppedGrid.enabled(enableInventControls);
    inventQtyGrp.enableChilds(enableInventControls);
    inventQtyGrpGrid.enableChilds(enableInventControls);
    inventOtherGrp.enableChilds(enableInventControls);
    InventLeadTimeGrpGrid.enableChilds(enableInventControls);

    this.pdsSetQtyControlsEnabledState(
                inventQtyGrp,
                inventQtyGrpCW,
                enableInventControls);
    this.pdsSetQtyControlsEnabledState(
                inventQtyGrpGrid,
                inventQtyGrpCWGrid,
                enableInventControls);
}

