public void enableSalesOverrideControls()
{
    if (!element.isSettingsDefault())
    {
        salesOverrideCheckbox.visible(true);
        salesParametersOverrideCheckbox.visible(false);
        salesLeadTimeOverrideCheck.visible(false);
        salesOtherGrp_M_editLeadTimeSiteSpecific.visible(true);

        salesLeadTimeGrpGrid.enableChilds(inventItemSalesSetup.Override);
        salesOverrideCheckboxGrid.enabled(true);
        SalesDeliveryOverrideCheckboxGrid.enabled(false);
        salesLeadTimeOverrideCheckboxGrid.enabled(false);

        salesQtyGrp.enableChilds(inventItemSalesSetup.Override);
        salesOtherGrp.enableChilds(inventItemSalesSetup.Override);
        salesDeliveryControlGrp.enableChilds(inventItemSalesSetup.Override);

        salesQtyGrpGrid.enableChilds(inventItemSalesSetup.Override);
        salesDeliveryControlGrpGrid.enableChilds(inventItemSalesSetup.Override);

        this.pdsSetQtyControlsEnabledState(
                salesQtyGrp,
                salesQtyGrpCW,
                salesOverrideCheckbox.checked());
        this.pdsSetQtyControlsEnabledState(
                salesQtyGrpGrid,
                salesQtyGrpCWGrid,
                salesOverrideCheckbox.checked());
    }
    else
    {
        salesParametersOverrideCheckbox.value(inventItemSalesSetup.Override);
        salesLeadTimeOverrideCheckbox.value(inventItemSalesSetup.OverrideSalesLeadTime);

        if (!RetailSMB::IsRetailSMBEnabled() && inventTable.ItemType != ItemType::Service)
        {
            salesParametersOverrideCheckbox.visible(true);
            SalesDeliveryOverrideCheckboxGrid.enabled(true);
        }

        salesOverrideCheckbox.visible(false);
        salesLeadTimeOverrideCheck.visible(true);
        salesOtherGrp_M_editLeadTimeSiteSpecific.visible(false);

        salesQtyGrp.enableChilds(true);
        salesOtherGrp.enableChilds(true);

        salesDeliveryControlGrp.enableChilds(inventItemSalesSetup.Override);

        salesQtyGrpGrid.enableChilds(true);
        salesOverrideCheckboxGrid.enabled(false);
        salesLeadTimeOverrideCheckboxGrid.enabled(true);
        salesLeadTimeGrpGrid.enableChilds(inventItemSalesSetup.OverrideSalesLeadTime);
        salesDeliveryControlGrpGrid.enableChilds(inventItemSalesSetup.Override);

        this.pdsSetQtyControlsEnabledState(
            salesQtyGrp,
            salesQtyGrpCW,
            true);
        this.pdsSetQtyControlsEnabledState(
            salesQtyGrpGrid,
            salesQtyGrpCWGrid,
            true);
        this.pdsSetQtyControlsEnabledState(
            inventQtyGrp,
            inventQtyGrpCW,
            true);
        this.pdsSetQtyControlsEnabledState(
            inventQtyGrpGrid,
            inventQtyGrpCWGrid,
            true);
        this.pdsSetQtyControlsEnabledState(
            purchQtyGrp,
            purchQtyGrpCW,
            true);
        this.pdsSetQtyControlsEnabledState(
            purchQtyGrpGrid,
            purchQtyGrpCWGrid,
            true);
    }

    salesStoppedGrid.enabled(inventItemSalesSetup.Override);
}

public void init()
{
    if (!element.args().caller())
    {
        throw error(strFmt("@SYS22678", element.name()));
    }

    if (   !(element.args().dataset() == tableNum(InventTable))
        && !(element.args().dataset() == tableNum(InventProductDefaultOrderSettingsStaging)))
    {
        throw error(strFmt("@SYS27080", element.name()));
    }

    this.initParameters();

    xSysLastValue::getLast(this);
    element.args().formViewOption(formViewOption);

    super();

    this.updateDesign(InventDimFormDesignUpdate::Init);

    this.initControls();

    element.applyToggles();
    element.enableViewSettingsButtons();

    if (formViewOption == FormViewOption::Grid && gridViewSelectedTab)
    {
        GridViewTabs.tab(gridViewSelectedTab);
    }
}

void initControls()
{
    if (inventTable.ItemType == ItemType::Service)
    {
        salesParametersOverrideCheckbox.visible(false);
        salesDeliveryControlGrp.visible(false);
        salesDeliveryControlGrpGrid.visible(false);
        inventParametersOverrideCheckbox.visible(false);
        inventDeliveryControlGrp.visible(false);
        inventDeliveryControlGrpGrid.visible(false);
    }
}

void initParameters()
{
    this.setInventTable();

    inventDimIdBlank     = InventDim::inventDimIdBlank();
}

