public void pdsSetQtyControlsEnabledState(
    FormGroupControl    _qtyGroupControl,
    FormGroupControl    _cwQtyGroupControl,
    boolean             _enabled)
{
    if (_enabled)
    {
        _qtyGroupControl.enableChilds(!isCWItem);
        _cwQtyGroupControl.enableChilds(isCWItem);
    }
    else
    {
        _qtyGroupControl.enableChilds(false);
        _cwQtyGroupControl.enableChilds(false);
    }
}

