void setInventTable()
{
    if (!element.args().caller())
    {
        throw error(strFmt("@SYS84645", element.name()));
    }

    if (   !(element.args().dataset() == tableNum(InventTable))
        && !(element.args().dataset() == tableNum(InventProductDefaultOrderSettingsStaging)))
    {
        throw error(strFmt("@SYS19096", element.name(), tableId2Name(element.args().dataset())));
    }

    if (element.args().dataset() == tableNum(InventProductDefaultOrderSettingsStaging))
    {
        InventProductDefaultOrderSettingsStaging inventProductDefaultOrderSettingsStaging = element.args().record();
        element.args().record(InventTable::find(inventProductDefaultOrderSettingsStaging.ItemNumber));
    }

    InventTable inventTableCaller = element.args().record() as InventTable;
    // Re-read as the caller record might not have been completely selected (in case it was part of a query with not all fields retrieved)
    inventTable = InventTable::find(inventTableCaller.ItemId);
    isCWItem = PdsGlobal::pdsIsCWItem(inventTable.ItemId);

    inventDimGroupSetup = InventDimGroupSetup::newInventTable(inventTable);
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParm;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:

            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl::newFromFormValidDataSource(element, InventDim_DS.name());
            }

            // do continue
       case InventDimFormDesignUpdate::Active:

            if (inventDimGroupSetup)
            {
                inventDimParm.initDimDefaultOrderSettings(inventDimGroupSetup);
            }

            inventDimFormSetup.parmDimParmVisibleGrid(inventDimParm);
            inventDimFormSetup.parmDimParmEnabled(inventDimParm);
            inventDimFormSetup.parmDimParmLockedRightClick(inventDimParm);
            inventDimFormSetup.parmDimParmVisible(inventDimParm);

            inventDimFormSetup.formSetControls(true);

            this.enableDimControls(); //enables inventDim controls, which are disabled by formSetControls(true)

            inventDim_ds.allowEdit(inventItemPurchSetup.RecId == 0);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

boolean validateInventDimInvent()
{
    boolean ret = InventStorageDimMap::checkWarehouseForInventDimPrimaryDefault(inventDim, inventDimInvent);

    if (ret)
    {
        this.setInventDimIdInvent();
        ret = inventItemInventSetup.validateField(fieldNum(InventItemInventSetup, InventDimIdDefault));
    }

    return ret;
}

