public int task(int _p1)
{
    #Task
    int ret;

    ret = super(_p1);

    if (_p1 == #taskSwitchToDetailsView)
    {
        formViewOption = FormViewOption::Details;
        element.enableViewSettingsButtons();
    }
    else if (_p1 == #taskSwitchToGridView)
    {
        formViewOption = FormViewOption::Grid;
        element.enableViewSettingsButtons();
        gridViewSelectedTab = 0;
    }

    return ret;
}

private void enableViewSettingsButtons()
{
    boolean enable = (formViewOption == FormViewOption::Grid);
    ButtonStorageDimToggle.enabled(enable);
    ButtonOrderQtyToggle.enabled(enable);
    ButtonLeadTimeToggle.enabled(enable);
    ButtonDeliveryControlToggle.enabled(enable);
}

public void create(boolean _append = false)
{
    InventItemSetupSequence newSequence = inventItemPurchSetup.getNextSequence();
    if (!newSequence)
    {
        InventItemPurchSetup::renumberSequencesAbove(inventItemPurchSetup.ItemId, inventItemPurchSetup.Sequence, 10);
        InventItemPurchSetup_ds.research(true);
        newSequence = inventItemPurchSetup.getNextSequence();
    }

    //Executing record creation
    super(_append);

    InventItemPurchSetup.Sequence = newSequence;

    //Shifting focus to the default(first) key field
    InventDim_InventSiteId.setFocus();
}

