/// <summary>
/// Controls the value of the <c>ctrlDefaultOrderType</c> <c>FormComboBoxControl</c>.
/// </summary>
/// <param name="_set">
/// A Boolean value that specifies whether a new value has been specified.
/// </param>
/// <param name="_inventItemSetupSupplyType">
/// A <c>InventItemSupplyType</c> record.
/// </param>
/// <param name="_value">
/// A <c>ReqPOType</c> enumeration value that holds the order type.
/// </param>
/// <returns>
/// A <c>ReqPOType</c> enumeration value that holds the order type.
/// </returns>
/// <remarks>
/// The <c>ctrlDefaultOrderType</c> <c>FormComboBoxControl</c> is used to present only the valid values of the <c>ReqPOType</c> enumerator.
/// </remarks>
public edit ReqPOType editOrderType(
    boolean                     _set,
    InventItemSetupSupplyType   _inventItemSetupSupplyType,
    ReqPOType                   _value)
{
    if (_set)
    {
        DictEnum dictEnum = new DictEnum(enumnum(ReqPOType));
        inventItemSetupSupplyType.initDefaultOrderType(dictEnum.name2Value(ctrlDefaultOrderType.getText(_value)));
    }

    if ( ctrlDefaultOrderType.selectText(enum2str(inventItemSetupSupplyType.DefaultOrderType)) < 0)
    {
        // the control doesn't have the db value -> add the selection in order to show the db value
        ctrlDefaultOrderType.add(enum2str(inventItemSetupSupplyType.DefaultOrderType));
    }

    return ctrlDefaultOrderType.selectText(enum2str(inventItemSetupSupplyType.DefaultOrderType));
}

public void write()
{
    if (ViewModeTab.tab() != 1 ||  element.isSettingsDefault())
    {
        inventItemSetupSupplyType.ItemDataAreaId    = inventTable.DataAreaId;
        inventItemSetupSupplyType.ItemId            = inventTable.ItemId;

        super();
    }
}

/// <summary>
/// Modifies the data source query adding link to the <c>InventItemPurchSetup</c> data source.
/// </summary>
public void init()
{
    super();

    QueryBuildDataSource queryBuildDataSource = this.query().dataSourceName(this.name());
    queryBuildDataSource.clearLinks();
    queryBuildDataSource.addLink(   fieldnum(InventItemPurchSetup, ItemId),
                                    fieldnum(InventItemSetupSupplyType, ItemId));
    queryBuildDataSource.addLink(   fieldnum(InventItemPurchSetup, DataAreaId),
                                    fieldnum(InventItemSetupSupplyType, ItemDataAreaId));
}

/// <summary>
/// Dynamically builds content of the control <c>ctrlDefaultOrderType</c>.
/// </summary>
/// <returns>
/// The record position in the data source.
/// </returns>
public int active()
{
    InventItemSetupSupplyType   itemSetupSupplyTypeTest;

    // builds the control with valid enum values
    ctrlDefaultOrderTypeGrid.clear();
    ctrlDefaultOrderType.clear();
    itemSetupSupplyTypeTest.data(inventItemSetupSupplyType);

    DictEnum dictEnum = new DictEnum(enumnum(ReqPOType));
    for (int i = 0; i < dictEnum.values(); i++)
    {
        itemSetupSupplyTypeTest.DefaultOrderType = i;
        if (   itemSetupSupplyTypeTest.checkDefaultOrderType(true)
            && isConfigurationkeyEnabled(dictEnum.index2ConfigurationKey(i)))
        {
            ctrlDefaultOrderTypeGrid.add(dictEnum.index2Label(i));
            ctrlDefaultOrderType.add(dictEnum.index2Label(i));
        }
    }

    ctrlDefaultOrderTypeGrid.selectText(enum2str(inventItemSetupSupplyType.DefaultOrderType));
    ctrlDefaultOrderType.selectText(enum2str(inventItemSetupSupplyType.DefaultOrderType));

    return super();
}

