void write()
{
    try
    {
        ttsbegin;

        super();

        if (lastEnteredReleaseDate != inventJournalTrans.ReleaseDate)
        {
            InventJournalTrans::adjustReleaseDate(inventJournalTrans);
            lastEnteredReleaseDate = inventJournalTrans.ReleaseDate;
        }

        ttscommit;

        int pos = inventJournalTrans_ds.getPosition();
        inventJournalTrans_ds.research(true);
        inventJournalTrans_ds.setPosition(pos);
    }
    catch (Exception::Error)
    {
        if (!inventJournalTrans.RecId)
        {
            inventJournalTrans.InventTransId = '';
        }
        throw Exception::Error;
    }
}

boolean validateWrite()
{
    boolean ret;

    inventJournalTrans.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    ret = super();

    if (inventJournalTrans.JournalType != InventJournalType::Asset)
    {
        ret = checkFailed(strfmt("@SYS22977",InventJournalType::Asset)) && ret;
    }

    if (!InventJournalTrans.AssetTransType)
    {
        ret = checkFailed("@FixedAssets:FA_Journal_Field_AssetTransType") && ret;
    }

    return ret;
}

void initValue()
{
    super();

    inventJournalTrans.JournalType= InventJournalType::Asset;

    if (lastEnteredReleaseDate != utcDateTimeNull())
    {
        inventJournalTrans.ReleaseDate = lastEnteredReleaseDate;
    }

    if (!inventJournalTrans.AssetTransType)
    {
        inventJournalTrans.AssetTransType = AssetTransTypeJournal::Acquisition;
    }
}

int active()
{
    int ret;

    ret = super();
    if (ret)
    {
        lastEnteredReleaseDate = inventJournalTrans.ReleaseDate;
    }

    return ret;
}

/// <summary>
/// Edits the asset transaction type.
/// </summary>
edit InventAssetJournalType editTransactionType(
    boolean _set,
    InventJournalTrans _inventJournalTrans,
    InventAssetJournalType _transType)
{
    InventAssetJournalType transType = _transType;

    if (_set)
    {
        if (transType == InventAssetJournalType::AcquisitionAdjustment)
        {
            _inventJournalTrans.AssetTransType = AssetTransTypeJournal::AcquisitionAdj;
        }
        else
        {
            _inventJournalTrans.AssetTransType = AssetTransTypeJournal::Acquisition;
        }
    }
    else
    {
        if (_inventJournalTrans.AssetTransType == AssetTransTypeJournal::AcquisitionAdj)
        {
            transType = InventAssetJournalType::AcquisitionAdjustment;
        }
        else
        {
            transType = InventAssetJournalType::Acquisition;
            _inventJournalTrans.AssetTransType = AssetTransTypeJournal::Acquisition;
        }
    }

    return transType;
}

