/// <summary>
/// Displays a warning if the entered date is in the past.
/// </summary>
///<remarks>
/// This method also saves the entered value in an internal variable.
///</remarks>
public void modified()
{
    super();
    if ((inventJournalTrans.ReleaseDate != utcDateTimeNull()) &&
         (DateTimeUtil::addDays(inventJournalTrans.ReleaseDate, 1) < DateTimeUtil::utcNow()))
    {
        warning("@SYS114595");
    }
}

