/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(tableStr(InventInventoryCountingJournalLineEntity),
                                                                CustomExportLinesToExcelOptionId);
    exportOption.displayName("@SYS22126");
    exportOptions.addEnd(exportOption);

    OfficeMenuDataEntityOptions officeMenuDataEntityOptions = OfficeMenuDataEntityOptions::construct(tableStr(InventInventoryCountingJournalLineEntity));
    officeMenuDataEntityOptions.includeDefault(false); //The default menu item for entity is already added via export menu item with id = CustomExportLinesToExcelOptionId
    if (countDataExportFlightEnabled)
    {
        officeMenuDataEntityOptions.dataSourceIdInternal(InventJournalTrans_ds.id());
    }
    _menuOptions.dataEntityOptions().addEnd(officeMenuDataEntityOptions);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    if (_menuItem.id() == CustomExportLinesToExcelOptionId)
    {
        context = ExportToExcelDataEntityContext::construct(tableStr(InventInventoryCountingJournalLineEntity), tableFieldGroupStr(InventInventoryCountingJournalLineEntity, AutoReport));

        ListEnumerator enumerator = context.entities().getEnumerator();
        while (enumerator.moveNext())
        {
            ExportToExcelDataEntityInfo entity = enumerator.current();

            if (entity.entityName() == tableStr(InventInventoryCountingJournalLineEntity))
            {
                ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(tablestr(InventInventoryCountingJournalLineEntity));

                var filter = filterBuilder.and(
                    filterBuilder.areEqual(fieldStr(InventInventoryCountingJournalLineEntity, DataAreaId), curExt()),
                    filterBuilder.areEqual(fieldStr(InventInventoryCountingJournalLineEntity, JournalNumber), InventJournalTable.JournalId));

                entity.filter(filter);
            }
        }
    }

    return context;
}

/// <summary>
/// Gets the set of initial filters to be applied to the template.
/// </summary>
/// <param name = "_menuItem">
/// The menu item for the template to apply the filters to.
/// </param>
/// <returns>
/// The Map of filters to apply keyed by AOT entity name.
/// </returns>
public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    Map filtersToApply = new Map(Types::String, Types::Class);

    if (_menuItem.dataEntityName() == tableStr(InventInventoryCountingJournalLineEntity)
        && (_menuItem.applyRecordFilter() || !countDataExportFlightEnabled))
    {
        ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(tableStr(InventInventoryCountingJournalLineEntity));

        var lineFilter = lineFilterBuilder.and(
                lineFilterBuilder.areEqual(fieldStr(InventInventoryCountingJournalLineEntity, JournalNumber), inventJournalTable.JournalId),
                lineFilterBuilder.areEqual(fieldStr(InventInventoryCountingJournalLineEntity, DataAreaId), inventJournalTable.DataAreaId));

        filtersToApply.insert(tableStr(InventInventoryCountingJournalLineEntity), lineFilter);

        if (countDataExportFlightEnabled)
        {
            // A record filter has already been applied
            _menuItem.applyRecordFilter(false);
        }
    }

    return filtersToApply;
}

