void init()
{
    xArgs args = this.args();

    // default journal type to ensure query durable between caller.
    if (args && !args.parmEnumType())
    {
        args.parmEnumType(enumNum(InventJournalType));
        args.parmEnum(enum2int(InventJournalType::Count));
    }

    inventJournalCountForm = InventJournalCountForm::construct();

    InventJournalFormTrans_Mov_Count journalFormTrans = this.constructInventJournalCountController();

    if (#PdsCatchWeightEnabled)
    {
        PdsCWFormCtrl cwFormCtrl = PdsCWFormCtrl::constructFromForm(element);
        cwFormCtrl.parmFieldIds(PdsCWValidation::fieldMapFromContainers(
            [[fieldnum(InventJournalTrans,Counted), fieldnum(InventJournalTrans,PdsCWInventQtyCounted)],
             [fieldnum(InventJournalTrans,Qty), fieldnum(InventJournalTrans,PdsCWQty)]] ));
        cwFormCtrl.parmItemFieldId(fieldnum(InventJournalTrans,ItemId));
        cwFormCtrl.parmUnitMethodStr(tablemethodstr(InventJournalTrans,pdsCWUnitId));

        journalFormTrans.parmCWFormCtrl(cwFormCtrl);
    }

    inventDimParmAllowCreate = true;

    var journalFormTable = new InventJournalFormTable(element);
    journalFormController = InventJournalFormController_Movement::newFromJournalForms(journalFormTable, journalFormTrans);

    super();

    journalFormController.parmCtrlVoucherGrid(inventJournalTrans_Voucher);
    journalFormController.parmCtrlVoucherTab(voucher);
    journalFormController.parmCtrlLineLogImage(lineLogImage);
    journalFormController.parmCtrlDeleteLines(inventJournalDeleteTrans);
    journalFormController.parmCtrlInventReserve(InventReserve);
    journalFormController.parmCtrlInventMarking(InventMarking);
    journalFormController.parmCtrlInventTransRegister(Register);
    journalFormController.parmCtrlInventTransPick(Pick);

    journalFormController.parmCtrlJournalTypeGrid(ctrlGridJournalType1);
    journalFormController.parmCtrlJournalIdGrid(ctrlGridJournalId1);
    journalFormController.parmCtrlCopyJournal(inventJournalCopy);
    journalFormController.parmCtrlReports_RU(printMenu);
    journalFormController.parmCtrlCheckJournal(checkJournal);
    journalFormController.parmCtrlPostJournal(postJournal);
    journalFormController.parmCtrlInfologTrans(showInfoLog);
    journalFormController.parmCtrlInfologTable(showInfoLogTable);
    journalFormController.parmCtrlMarkUserBlock(markUserBlock);
    journalFormController.parmCtrlMarkInUseBlock(markInUseBlock);
    journalFormController.parmCtrlSystemBlocked(systemBlocked);
    journalFormController.parmCtrlBlockImage(blockImage);
    journalFormController.parmCtrlInitTotals(initTotals);

    journalFormTable.parmCtrlQueries_RU(queries_RU);
    journalFormTable.parmCtrlReports_RU(printMenu);

    journalFormController.formInitPost();

    this.initOrigWorkflowRelatedControllerStatus();
}

private void initOrigWorkflowRelatedControllerStatus()
{
    inventJournalTableAllowEdit = inventJournalTable_ds.allowEdit();
    inventJournalTransAllowEdit = inventJournalTrans_ds.allowEdit();
    inventDimAllowEdit = inventDim_ds.allowEdit();
    postJournalEnabled = postJournal.enabled();
    requestChangeEnabled = InventJournalRequestChange.enabled();
    deleteTransEnabled = InventJournalDeleteTrans.enabled();
}

/// <summary>
/// Reset button status to original enabled values.
/// </summary>
public void resetWorkflowImpactButtons()
{
    if (InventJournalTable.Posted == NoYes::No)
    {
        postJournal.enabled(postJournalEnabled);
    }
    else
    {
        postJournal.enabled(false);
    }

    InventJournalRequestChange.enabled(requestChangeEnabled);
    InventJournalDeleteTrans.enabled(deleteTransEnabled);
}

/// <summary>
/// Disallow any change for the journals that are in not editable workflow status.
/// </summary>
public void disallowChangeOnJournal()
{
    InventJournalDeleteTrans.enabled(false);
    inventJournalTable_ds.allowEdit(false);
    inventJournalTrans_ds.allowEdit(false);
    inventDim_ds.allowEdit(false);
    inventJournalTable_ds.allowDelete(false);
    inventJournalTrans_ds.allowDelete(false);
    inventJournalTrans_ds.allowCreate(false);
}

/// <summary>
/// Enables and disables controls on the form.
/// </summary>
/// <remarks>
/// This method is called from the enableButtonsActive method of the JournalFormTable class. The
/// JournalFormTable.enableButtonsActive method is called by the active method on the data source
/// of this form.
/// The form functionality is limited by the form context and the rights of the security model.
/// </remarks>
void enableButtonsActive()
{
    printMenu.enabled(inventJournalTable.RecId != 0);
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        this.enableButtonsActive_RU();
    }

    var journalFormTrans = journalFormController.parmJournalFormTrans();
    boolean isNewlyCreatedJournal = journalFormTrans.journalTableData() &&
                                    journalFormTrans.journalTable().NumOfLines == 0 &&
                                    journalFormTrans.journalTable().Posted == false;  //Posted journals without lines exist if "Delete lines after posting" is true.

    count_MenuButton.enabled(isNewlyCreatedJournal);

    postJournal.menuItemName(menuitemActionStr(InventJournalPostCount));

    this.resetWorkflowImpactButtons();

    boolean isWorkflowUsed = inventJournalTable.isWorkflowUsed();
    if (isWorkflowUsed && InventJournalTable.Posted == NoYes::No
        && inventJournalTableAllowEdit && inventJournalTransAllowEdit)
    {
        InventJournalTableWFDropDialog.enabled(true);
        if (!inventJournalTable.editableInWorkflow())
        {
            this.disallowChangeOnJournal();
        }

        if (inventJournalTable.isWFApprovalApproved())
        {
            InventJournalRequestChange.enabled(true);
        }
        else
        {
            postJournal.enabled(false);
        }
    }
    else if (inventJournalTable.needEnableWFDropDialog(isWorkflowUsed))
    {
        InventJournalTableWFDropDialog.enabled(true);
    }
    else
    {
        InventJournalTableWFDropDialog.enabled(false);
    }
}

