public void initValue()
{
    InventJournalTable inventJournalTableLocal; // Temp access to associated InventJournalTable

    super();

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        // For any new line - Get the InventJournalTable so we can pre-populate the InventSiteId and InventWarehouseId
        inventJournalTableLocal = inventJournalTrans.inventJournalTable();
        InventDim.InventSiteId = inventJournalTableLocal.InventSiteId;
        InventDim.InventLocationId = inventJournalTableLocal.InventLocationId;
    }
}

