void clicked()
{
    FormRun formRun;

    JournalForm journalTrans = element.journalForm();
    journalTrans.journalTable().reread();

    formRun = classFactory.formRunClass(new Args(formstr(RetailAddItems)));

    inventjournaltrans.clear();
    inventJournalTrans.initValue();
    inventJournalTrans.initFromInventJournalTable(journalFormController.parmInventJournalFormTable().journalTable()); // Access the journalFormTrans journalTable()

    formRun.args().caller(element);
    formRun.args().record(inventJournalTrans);
    formRun.args().menuItemName(menuitemDisplayStr(RetailAddItems));
    formRun.init();
    formRun.run();
    formRun.wait();

    InventJournalTable_DS.reread();
}
