/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>

/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    TableName adjustmentEntry = tableStr(InventInventoryAdjustmentJournalEntryV2Entity);
    List exportOptions = _menuOptions.customMenuItems();

    var exportOptionAdjustmentV2 = OfficeGeneratedExportMenuItem::construct(adjustmentEntry, CustomExportLinesToExcelOptionV2Id);
    exportOptionAdjustmentV2.setDisplayNameWithDataEntity();

    exportOptions.addEnd(exportOptionAdjustmentV2);

    OfficeMenuDataEntityOptions entityContext = OfficeMenuDataEntityOptions::construct(tableStr(InventInventoryAdjustmentJournalEntryV2Entity));
    entityContext.includeDefault(false);
    entityContext.dataSourceIdInternal(InventJournalTrans_ds.id());
    _menuOptions.dataEntityOptions().addEnd(entityContext);
}

/// <summary>
/// Gets the set of initial filters to be applied to the template.
/// </summary>
/// <returns>
/// The Map of filters to apply keyed by AOT entity name.
/// Values are all Microsoft.Dynamics.Platform.Integration.Office.FilterNode intances.
/// </returns>
/// <remarks>
/// The filters supplied here will override any filters currently in the template.
/// If no filter is supplied for a given entity, any filters currently in the template are retained.
/// </remarks>
public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    Map filtersToApply = new Map(Types::String, Types::Class);

    if (_menuItem.dataEntityName() == tableStr(InventInventoryAdjustmentJournalEntryV2Entity)
        && _menuItem.applyRecordFilter())
    {
        ExportToExcelFilterTreeBuilder filterBuilder = new ExportToExcelFilterTreeBuilder(tableStr(InventInventoryAdjustmentJournalEntryV2Entity));

        var filter = filterBuilder.and(
                filterBuilder.areEqual(fieldStr(InventInventoryAdjustmentJournalEntryV2Entity, DataAreaId), curExt()),
                filterBuilder.areEqual(fieldStr(InventInventoryAdjustmentJournalEntryV2Entity, JournalNumber), InventJournalTable.JournalId));

        filtersToApply.insert(tableStr(InventInventoryAdjustmentJournalEntryV2Entity), filter);

        // A record filter has already been applied
        _menuItem.applyRecordFilter(false);
    }
    return filtersToApply;
}

