/// <summary>
/// Allows the form to implement form specific logic that can be ran after the controller has handled the modified field event.
/// </summary>
/// <param name = "_formDataObject">The <c>FormDataObject</c> that was modified.</param>
public void fieldModifiedEventHandler(FormDataObject _formDataObject)
{
    switch (_formDataObject.datasource().table())
    {
        case tableNum(InventJournalTrans) :
            this.inventJournalTransFieldModified(_formDataObject.fieldId());
            break;

        default:
            break;
    }
}

private void inventJournalTransFieldModified(FieldId _fieldId)
{                
    switch (_fieldId)
    {
        case InventJournalTrans_ItemId.dataField() :
            journalFormController.initInventDimFromJournalTable();
            break;

        default:
            break;
    }
}

void init()
{
    xArgs args = this.args();

    // default journal type to ensure query durable between caller.
    if (args && !args.parmEnumType())
    {
        args.parmEnumType(enumNum(InventJournalType));
        args.parmEnum(enum2int(InventJournalType::LossProfit));
    }

    PdsCWFormCtrl   cwFormCtrl;

    var journalFormTrans = new InventJournalFormTrans_Movement(element);
    var journalFormTable = new InventJournalFormTable(element);

    journalFormController = InventJournalFormController_Movement::newFromJournalForms(journalFormTable, journalFormTrans);
    journalFormController.fieldModified += eventhandler(this.fieldModifiedEventHandler);

    if (#PdsCatchWeightEnabled)
    {
        cwFormCtrl = PdsCWFormCtrl::constructFromForm(element);

        cwFormCtrl.parmFieldIds(PdsCWValidation::fieldMapFromContainers(
            [[fieldNum(InventJournalTrans,Qty), fieldNum(InventJournalTrans,PdsCWQty)]] ));
        cwFormCtrl.parmItemFieldId(fieldNum(InventJournalTrans,ItemId));
        cwFormCtrl.parmUnitMethodStr(tableMethodStr(InventJournalTrans,pdsCWUnitId));

        journalFormTrans.parmCWFormCtrl(cwFormCtrl);
    }

    super();

    inventDimParmAllowCreate = true;

    journalFormController.parmCtrlVoucherGrid(inventJournalTrans_Voucher);
    journalFormController.parmCtrlVoucherTab(voucher);
    journalFormController.parmCtrlLineLogImage(lineLogImage);
    journalFormController.parmCtrlDeleteLines(inventJournalDeleteTrans);
    journalFormController.parmCtrlInventReserve(Reserve);
    journalFormController.parmCtrlInventMarking(InventMarking);
    journalFormController.parmCtrlInventTransRegister(Register);
    journalFormController.parmCtrlInventTransPick(Pick);

    journalFormController.parmCtrlJournalTypeGrid(ctrlGridJournalType1);
    journalFormController.parmCtrlJournalIdGrid(ctrlGridJournalId1);
    journalFormController.parmCtrlInfologTable(showInfoLogTable);
    journalFormController.parmCtrlInfologTrans(showInfoLog);
    journalFormController.parmCtrlCheckJournal(checkJournal);
    journalFormController.parmCtrlPostJournal(postJournal);
    journalFormController.parmCtrlMarkUserBlock(markUserBlock);
    journalFormController.parmCtrlMarkInUseBlock(markInUseBlock);
    journalFormController.parmCtrlSystemBlocked(systemBlocked);
    journalFormController.parmCtrlBlockImage(blockImage);
    journalFormController.parmCtrlInitTotals(initTotals);
    journalFormController.parmCtrlCopyJournal(inventJournalCopy);
    journalFormController.parmCtrlReports_RU(printMenu);

    journalFormTable.parmCtrlQueries_RU(queries_RU);
    journalFormTable.parmCtrlReports_RU(printMenu);

    journalFormController.formInitPost();

    if (!InventJournalUnitOfMeasureFeature::isEnabled()) 
    {
        InventJournalTrans_Unit.visible(false);
        InventJournalTrans_UnitQty.visible(false);

        JournalLine_Unit.visible(false);
        JournalLine_UnitQty.visible(false);
    }

    this.initOrigWorkflowRelatedControllerStatus();
}

private void initOrigWorkflowRelatedControllerStatus()
{
    inventJournalTableAllowEdit = inventJournalTable_ds.allowEdit();
    inventJournalTransAllowEdit = inventJournalTrans_ds.allowEdit();
    inventDimAllowEdit = inventDim_ds.allowEdit();
    postJournalEnabled = postJournal.enabled();
    requestChangEnabled = InventJournalRequestChange.enabled();
    deleteTransEnabled = InventJournalDeleteTrans.enabled();
}

