/// <summary>
/// Reset button status to original enabled values.
/// </summary>
public void resetWorkflowImpactButtons()
{
    if (InventJournalTable.Posted == NoYes::No)
    {
        postJournal.enabled(postJournalEnabled);
    }
    else
    {
        postJournal.enabled(false);
    }
    InventJournalRequestChange.enabled(requestChangEnabled);
    InventJournalDeleteTrans.enabled(deleteTransEnabled);
}

/// <summary>
/// Disallow any change for the journals that are in not editable workflow status.
/// </summary>
public void disallowChangeOnJournal()
{
    InventJournalDeleteTrans.enabled(false);
    inventJournalTable_ds.allowEdit(false);
    inventJournalTrans_ds.allowEdit(false);
    inventDim_ds.allowEdit(false);
    inventJournalTable_ds.allowDelete(false);
    inventJournalTrans_ds.allowDelete(false);
    inventJournalTrans_ds.allowCreate(false);
}

/// <summary>
/// Enables and disables controls on the form.
/// </summary>
/// <remarks>
/// This method is called from the enableButtonsActive method of the JournalFormTable class. The
/// JournalFormTable.enableButtonsActive method is called by the active method on the data source
/// of this form.
/// The form functionality is limited by the form context and the rights of the security model.
/// </remarks>
void enableButtonsActive()
{
    printMenu.enabled(inventJournalTable.RecId != 0);
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        this.enableButtonsActive_RU();
    }

    postJournal.menuItemName(menuitemActionStr(InventJournalPostLossProfit));

    PdsResetDispositionStatus.enabled(journalFormController.isJournalFree());

    this.resetWorkflowImpactButtons();

    boolean isWorkflowUsed = inventJournalTable.isWorkflowUsed();
    if (isWorkflowUsed && InventJournalTable.Posted == NoYes::No
    && inventJournalTableAllowEdit && inventJournalTransAllowEdit && inventDimAllowEdit)
    {
        InventJournalTableWFDropDialog.enabled(true);
        if (!inventJournalTable.editableInWorkflow())
        {
            this.disallowChangeOnJournal();
        }

        if (inventJournalTable.isWFApprovalApproved())
        {
            InventJournalRequestChange.enabled(true);
        }
        else
        {
            postJournal.enabled(false);
        }
    }
    else if (inventJournalTable.needEnableWFDropDialog(isWorkflowUsed))
    {
        InventJournalTableWFDropDialog.enabled(true);
    }
    else
    {
        InventJournalTableWFDropDialog.enabled(false);
    }
}

void enableButtonsActive_RU()
{
    queries_RU.enabled(inventJournalTable.RecId != 0);
}

