void pdsDispoShow()
{
    if (inventoryDimensionsGrid_inventBatchId.visible())
    {
        //Only change visibility when needed to
        //minimise trigger of 'modified' events
        if (!pdsDispositionCode.visible())
        {
            pdsDispositionCode.visible(true);
        }
        if (!pdsDispositionStatus.visible())
        {
            pdsDispositionStatus.visible(true);
        }
    }
    else
    {
        if (pdsDispositionCode.visible())
        {
            pdsDispositionCode.visible(false);
        }
        if (pdsDispositionStatus.visible())
        {
            pdsDispositionStatus.visible(false);
        }
    }
}

