void enableFields()
{
    switch (inventJournalName.JournalType)
    {
        case InventJournalType::Movement:
            inventJournalName_ds.object(fieldNum(InventJournalName, LedgerDimension)).enabled(true);
            inventJournalName_ds.object(fieldNum(InventJournalName, Reservation)).enabled(true);
            inventJournalName_ds.object(fieldNum(InventJournalName, DetailSummary)).enabled(true);
            inventJournalName_ds.object(fieldNum(InventJournalName, RetailInventJournalPosAdjustmentType)).enabled(true);
            break;
        case InventJournalType::TagCounting:
            inventJournalName_ds.object(fieldNum(InventJournalName, LedgerDimension)).enabled(false);
            inventJournalName_ds.object(fieldNum(InventJournalName, Reservation)).enabled(false);
            inventJournalName_ds.object(fieldNum(InventJournalName, DetailSummary)).enabled(true);
            inventJournalName_ds.object(fieldNum(InventJournalName, RetailInventJournalPosAdjustmentType)).enabled(false);
            break;
        case InventJournalType::OwnershipChange:
            inventJournalName_ds.object(fieldNum(InventJournalName, LedgerDimension)).enabled(false);
            inventJournalName_ds.object(fieldNum(InventJournalName, Reservation)).enabled(false);
            inventJournalName_ds.object(fieldNum(InventJournalName, DetailSummary)).enabled(false);
            inventJournalName_ds.object(fieldNum(InventJournalName, RetailInventJournalPosAdjustmentType)).enabled(false);
            break;
        case InventJournalType::LossProfit:
            inventJournalName_ds.object(fieldNum(InventJournalName, LedgerDimension)).enabled(false);
            inventJournalName_ds.object(fieldNum(InventJournalName, Reservation)).enabled(true);
            inventJournalName_ds.object(fieldNum(InventJournalName, DetailSummary)).enabled(true);
            inventJournalName_ds.object(fieldNum(InventJournalName, RetailInventJournalPosAdjustmentType)).enabled(true);
            break;
        default:
            inventJournalName_ds.object(fieldNum(InventJournalName, LedgerDimension)).enabled(false);
            inventJournalName_ds.object(fieldNum(InventJournalName, Reservation)).enabled(true);
            inventJournalName_ds.object(fieldNum(InventJournalName, DetailSummary)).enabled(true);
            inventJournalName_ds.object(fieldNum(InventJournalName, RetailInventJournalPosAdjustmentType)).enabled(false);
    }

    inventJournalName_ds.object(fieldNum(InventJournalName, CountingStatusRegistrationPolicy)).enabled(InventJournalTable::canJournalTypeUseCountingStatusRegistration(inventJournalName.JournalType));

}

public void init()
{
    super();
    posting_LedgerAccountIdOffset.parmPostingType(LedgerPostingType::None);
    // <GEERU>
    inventJournalNameForm = new InventJournalNameForm_RU(this, availableReports, selectedReports);
    // </GEERU>
    element.enableFields();
    ApprovalWorkflow.caption("@SYS109575");
}

public boolean dialogYesNo(str _message)
{
    boolean ret = true;

    DialogButton dialogBox = Box::yesNo(_message, DialogButton::Yes, "@SYS866");

    if (dialogBox == DialogButton::No)
    {
        ret = false;
    }

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    element.enableFields();

    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([ #isoRU ]))
    {
        inventJournalNameForm.inventJournalName(inventJournalName);
        inventJournalNameForm.updateControls();
    }

    // </GEERU>
    return ret;
}

/// <summary>
/// Lookup of availabe workflows for this journal type.
/// </summary>
/// <param name = "_formControl"></param>
/// <param name = "_filterStr"></param>
public void lookup(FormControl _formControl, str _filterStr)
{

    Query                           query          = new Query();
    QueryBuildDataSource            queryBuildDataSource;
    str                             moduleRangeValueStr;
    InventJournalWorkflowApprovalModule   module = InventJournalName::findWorkflowApprovalModule(inventJournalName.JournalType);

    // Create an instance of SysTableLookup with the "lookup table" and
    // the current form control.
    SysTableLookup sysTableLookup =
        SysTableLookup::newParameters(tableNum(WorkflowTable), _formControl);

    // Add fields to be shown in the lookup form.
    sysTableLookup.addLookupfield(fieldNum(WorkflowTable, SequenceNumber));
    sysTableLookup.addLookupfield(fieldNum(WorkflowTable, Name));
    sysTableLookup.addLookupfield(fieldNum(WorkflowTable, TemplateName));
    sysTableLookup.addLookupfield(fieldNum(WorkflowTable, Module));

    // Create the QueryBuildDataSource.
    queryBuildDataSource = query.addDataSource(tableNum(WorkflowTable));

    // Add fields to order the data in the lookup form.
    queryBuildDataSource.addOrderByField(fieldNum(WorkflowTable, SequenceNumber));

    // Add fields to select the existing data in the lookup form.
    queryBuildDataSource.addSelectionField(fieldNum(WorkflowTable, SequenceNumber));

    // Limit and arrange the data selection.
    queryBuildDataSource.addRange(fieldNum(WorkflowTable, TemplateName)).
        value(InventJournalName::findJournalTemplate(inventJournalName.JournalType));

    // Set the right string value for the ModuleAxapta enumeration.
    moduleRangeValueStr = this.getModuleRangeValue(module);

    queryBuildDataSource.addRange(fieldNum(WorkflowTable, DataArea)).value(curext());
    queryBuildDataSource.addRange(fieldNum(WorkflowTable, Module)).value(moduleRangeValueStr);
    queryBuildDataSource.addRange(fieldNum(WorkflowTable, Type)).
        value(int2str(enum2int(WorkflowConfigurationType::Definition)));

    // Set the query.
    sysTableLookup.parmQuery(query);

    // Perform the lookup.
    sysTableLookup.performFormLookup();
}

/// <summary>
/// Get the module of workflow supported journal types.
/// </summary>
/// <param name = "_module"></param>
/// <returns></returns>
[Wrappable(true)]
protected final str getModuleRangeValue(InventJournalWorkflowApprovalModule _module)
{
    str moduleRangeValueStr;
    const str notSupported = "@SYS91571";

    switch (_module)
    {
        case InventJournalWorkflowApprovalModule::Movement:
        case InventJournalWorkflowApprovalModule::LossProfit:
        case InventJournalWorkflowApprovalModule::Transfer:
        case InventJournalWorkflowApprovalModule::BOM:
        case InventJournalWorkflowApprovalModule::OwnershipChange:
        case InventJournalWorkflowApprovalModule::Count:
        case InventJournalWorkflowApprovalModule::TagCounting:
            moduleRangeValueStr = int2str(enum2int(ModuleAxapta::Inventory));
            break;

        default:
            moduleRangeValueStr = notSupported;
            break;
    }

    return moduleRangeValueStr;
}

/// <summary>
/// Overridden to provide additional validation for changes to the field's data
/// when journal name references a configuration and workflow approvals are "active".
/// </summary>
/// <returns>
/// True if all of the validations are successful, otherwise false.
/// </returns>
/// <remarks>
/// Code added to inform the user that deletion may cause changes to any workflow approval
/// associations to this JournalName record.
/// </remarks>
public boolean validate()
{
    boolean ret = super();

    if (ret)
    {
        if (inventJournalName.RecId != 0 &&
            inventJournalName.WorkflowApproval)
        {
            if (InventJournalName.existRelatedNotPostedJournal())
            {
                ret = checkFailed("@SCM:CannotChangeWFforNotPostedJournals");
            }
            else
            {
                str message = "@SYS109583";
                ret = element.dialogYesNo(message);
            }
        }
    }

    return ret;
}

public boolean validate()
{
    boolean ret = super();
    if (ret && inventJournalName.RecId != 0)
    {
        if (InventJournalName.existRelatedNotPostedJournal())
        {
            ret = checkFailed("@SCM:CannotChangeWFforNotPostedJournals");
        }
        else
        {
            // Check if ok to change workflow approval.
            str message = "@SCM:CheckIfOKtoChangeWF";
            ret = element.dialogYesNo(message);
        }

    }
    return ret;
}
