/// <summary>
/// Customizes the options used to populate the Office Menu.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityIterator.more())
    {
        dataEntityIterator.delete();
    }

    DocuTemplate docuTemplate = DocuTemplate::findTemplate(OfficeAppApplicationType::Excel, resourceStr(ProjectItemJournalEntryTemplate));

    if (docuTemplate)
    {
        OfficeTemplateExportMenuItem menuItem = OfficeTemplateExportMenuItem::constructWithDocuTemplate(docuTemplate, docuTemplate.TemplateID);
        _menuOptions.customMenuItems().addEnd(menuItem);
    }
}

/// <summary>
/// Gets the set of initial filters to be applied to the template.
/// </summary>
/// <param name = "_menuItem">
/// The menu item for the template to apply the filters to.
/// </param>
/// <returns>
/// The Map of filters to apply keyed by AOT entity name.
/// </returns>
public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    const str templateName = resourceStr(ProjectItemJournalEntryTemplate);

    DocuTemplate template = DocuTemplate::findTemplate(OfficeAppApplicationType::Excel, templateName);
    Map filtersToApply = new Map(Types::String, Types::Class);
    if (template && template.TemplateID == templateName)
    {
        InventJournalTable journalTable = journalFormTrans.getJournalFormTable().getJournalFormTable().
                                                   journalTableData().journalTable();
        ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(tableStr(ProjectItemJournalTransEntity));

        Microsoft.Dynamics.Platform.Integration.Office.FilterCollectionNode lineFilter = lineFilterBuilder.and(
            lineFilterBuilder.areEqual(fieldStr(ProjectItemJournalTransEntity,JournalId), journalTable.JournalId),
            lineFilterBuilder.areEqual(fieldStr(ProjectItemJournalTransEntity,DataAreaId), curExt()));

        filtersToApply.insert(tableStr(ProjectItemJournalTransEntity), lineFilter);

        ExportToExcelFilterTreeBuilder headerFilterBuilder = new ExportToExcelFilterTreeBuilder(tableStr(ProjectItemJournalTableEntity));

        Microsoft.Dynamics.Platform.Integration.Office.FilterCollectionNode headerFilter = headerFilterBuilder.and(
            headerFilterBuilder.areEqual(fieldStr(ProjectItemJournalTableEntity,JournalId), journalTable.JournalId),
            headerFilterBuilder.areEqual(fieldStr(ProjectItemJournalTableEntity,DataAreaId), journalTable.DataAreaId));

        filtersToApply.insert(tableStr(ProjectItemJournalTableEntity), headerFilter);
    }
    return filtersToApply;
}

