void init()
{
    journalFormTrans = new InventJournalFormTrans_Movement(element);

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        isIndiaIndirectTaxParameterMarked   = TaxParameters::checkTaxParameters_IN();
        isIndiaWHTMarked                    = TaxWithholdParameters_IN::checkTaxParameters();
        isInventJournalProj_INEnabled       = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(InventJournalTransTaxExtensionIN));
    }
    // </GIN>

    super();

    // <GTE>
    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        TransTaxInformation.visible(true);
    }
    // </GTE>

    journalFormTrans.parmCtrlCheckJournal(checkJournal);
    journalFormTrans.parmCtrlPostJournal(postJournal);
    journalFormTrans.parmCtrlInfolog(showInfolog);
    journalFormTrans.parmCtrlVoucherGrid(inventJournalTrans_Voucher);
    journalFormTrans.parmCtrlVoucherTab(voucher);
    journalFormTrans.parmCtrlLineLogImage(lineLogImage);
    journalFormTrans.parmCtrlDeleteLines(inventJournalDeleteTrans);

    journalFormTrans.formInitPost();

    cdsLockingOptionItemJournals = ProjCDSLockingOptionTable::get(ProjCDSLockingOption::ItemJournals);

    // <GIN> 
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        element.setVisibleTaxInformation_IN();
        element.setTaxWithholdVisible_IN();
    }
    // </GIN>

    // <GTE>
    if (TaxSolutionScopeIntegrationUtil::isCompanyEnabled())
    {
        ProjectTax.visible(false);
        ProjectTax_ProjTaxGroupId.visible(false);
        ProjectTax_ProjTaxItemGroupId.visible(false);
    }
    // </GTE>
}

void setEditSalesCurrencyIdAllowed()
{
    if (ProjInvoiceTable::isSalesCurrencyLocked(ProjTable::find(inventJournalTrans.ProjId).ProjInvoiceProjId))
    {
        inventJournalTrans_ds.object(fieldNum(InventJournalTrans, ProjSalesCurrencyId)).allowEdit(false);
        inventJournalTrans_ds.object(fieldNum(InventJournalTrans, ProjSalesCurrencyId)).skip(true);
    }
    else
    {
        inventJournalTrans_ds.object(fieldNum(InventJournalTrans, ProjSalesCurrencyId)).allowEdit(true);
        inventJournalTrans_ds.object(fieldNum(InventJournalTrans, ProjSalesCurrencyId)).skip(false);
    }
}

void setTaxWithholdVisible_IN()
{
    TaxWithholdParameters_IN    taxWithholdParameters;

    taxWithholdParameters = TaxWithholdParameters_IN::find();
    inventJournalTransTaxExtensionIN_ds.object(fieldNum(InventJournalTransTaxExtensionIN, TDSGroup)).visible(taxWithholdParameters.tdsActivate);
    inventJournalTransTaxExtensionIN_ds.object(fieldNum(InventJournalTransTaxExtensionIN, TCSGroup)).visible(taxWithholdParameters.tcsActivate);
}

