/// <summary>
///    Performs a lookup on the string control that is passed in as a parameter.
/// </summary>
/// <param name="_ctrl">
///    The string control on which to perform the lookup.
/// </param>
/// <param name="_buffer">
///    The data to pass to the lookup form.
/// </param>
/// <remarks>
///    The <c>ProjtableLookup</c> form is used to display the lookup list for this string control.
/// </remarks>
private void lookupProjId(FormControl _ctrl, Common _buffer)
{
    FormStringControl   formStringControl;
    Args                args = new Args();
    FormRun             formRun;

    args.name(formStr(ProjTableLookup));
    args.caller(_ctrl);
    args.record(_buffer);
    args.parmEnum(NoYes::No);

    formRun = classfactory.formRunClass(args);
    formRun.init();

    if (cdsLockingOptionItemJournals)
    {
        FormDataSource formDS = formRun.dataSource(1);
        QueryBuildDataSource qbds1 = formDS.queryBuildDataSource();
        QueryBuildDataSource qbds2 = qbds1.addDataSource(tableNum(ProjTable));
        qbds2.addLink(fieldNum(ProjProjectLookup, ProjId), fieldNum(ProjTable, ProjId));
        qbds2.joinMode(JoinMode::InnerJoin);
        qbds2.addRange(fieldNum(ProjTable, ProjDataSource)).value(SysQuery::value(0));
    }

    formStringControl = _ctrl as FormStringControl;
    formStringControl.performFormLookup(formRun);
}

private boolean validateProjectExternallyManaged(ProjId _projId)
{
    boolean ret = true;

    if (cdsLockingOptionItemJournals)
    {
        if (Project::isProjectExternallyManaged(_projId))
        {
            ret = checkFailed(strfmt("@Proj:ProjCDSValueInvalidDueToExternal", "@Proj:ProjCDSItemJournals"));
        }
    }
    return ret;
}

void write()
{
    try
    {
        journalFormTrans.datasourceWritePre();

        ttsbegin;

        super();

        journalFormTrans.datasourceWritePost();

        // <GIN>
        if (isInventJournalProj_INEnabled)
        {
            InventJournalTransTaxExtensionIN.SysExtensionSerializerExtensionMap::insertAfterBaseTable(inventJournalTrans);
        }
        // </GIN>

        ttscommit;
    }
    catch (Exception::Error)
    {
        if (!inventJournalTrans.RecId)
        {
            inventJournalTrans.InventTransId = '';
        }
        throw Exception::Error;
    }
}

boolean validateWrite()
{
    boolean ret;

    inventJournalTrans.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    ret = super();

    ret = journalFormTrans.datasourceValidateWritePost(ret);

    return ret;
}

void initValue()
{
    FormRun     caller,
                parentCaller;

    ProjTable   localProjTable;

    journalFormTrans.datasourceInitValuePre();

    super();

    journalFormTrans.datasourceInitValuePost();

    inventJournalTrans.ProjCategoryId = ProjParameters::find().ItemCategory;

    if (element.args())
    {
        caller = element.args().caller();
        if (caller && caller.args())
        {
            parentCaller = caller.args().caller();

            if (parentCaller && caller.args().dataset() == tableNum(ProjTable))
            {
                localProjTable = caller.args().record();
                inventJournalTrans.ProjId = localProjTable.ProjId;
                inventJournalTrans.initFromProjTable(localProjTable);
            }
        }
    }
}

void delete()
{
    ttsbegin;

    journalFormTrans.datasourceDeletePre();

    super();

    journalFormTrans.datasourceDeletePost();

    ttscommit;
}

void create(boolean append = false)
{
    journalFormTrans.datasourceCreatePre();

    super(append);
}

int active()
{
    int ret = super();

    journalFormTrans.datasourceActivePost();
    element.setEditSalesCurrencyIdAllowed();

    InventJournalTrans journalTrans = this.cursor();
    boolean isRecordExternallyManaged = Project::isProjectExternallyManaged(journalTrans.ProjId);
    boolean lockFieldsForItemJournals = cdsLockingOptionItemJournals && isRecordExternallyManaged;

    InventJournalTrans_ds.allowDelete(!lockFieldsForItemJournals);
    InventJournalTrans_ds.allowEdit(!lockFieldsForItemJournals);

    // <GIN>
    if (isIndiaWHTMarked)
    {
        element.setTaxWithholdEnabled_IN();
    }
    // </GIN>

    return ret;
}

