/// <summary>
/// Enables and disables controls on the form.
/// </summary>
/// <remarks>
/// This method is called from the enableButtonsActive method of the JournalFormTable class. The
/// JournalFormTable.enableButtonsActive method is called by the active method on the data source
/// of this form.
/// The form functionality is limited by the form context and the rights of the security model.
/// </remarks>
void enableButtonsActive()
{
    InventJournalType journalType = journalFormTable.journalTypeId();

    printMenu.enabled(inventJournalTable.RecId != 0);
    // <GEERU>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
    {
        this.enableButtonsActive_RU();
    }
    // </GEERU>

    switch (journalType)
    {
        case InventJournalType::Asset:
            postJournal.menuItemName(menuitemActionStr(InventJournalPostAsset));
            break;

        case InventJournalType::BOM:
            postJournal.menuItemName(menuitemActionStr(InventJournalPostBOM));
            break;

        case InventJournalType::Count:
            postJournal.menuItemName(menuitemActionStr(InventJournalPostCount));
            break;

        case InventJournalType::LossProfit:
            postJournal.menuItemName(menuitemActionStr(InventJournalPostLossProfit));
            break;

        case InventJournalType::Movement:
            postJournal.menuItemName(menuitemActionStr(InventJournalPostMovement));
            break;

        case InventJournalType::project:
            postJournal.menuItemName(menuitemActionStr(InventJournalPostProject));
            break;

        case InventJournalType::TagCounting:
            postJournal.menuItemName(menuitemActionStr(InventJournalPostCountTag));
            break;

        case InventJournalType::Transfer:
            postJournal.menuItemName(menuitemActionStr(InventJournalPostTransfer));
            break;
        default:
            postJournal.enabled(false);
    }
}

void enableButtonsActive_RU()
{
    queries_RU.enabled(inventJournalTable.RecId != 0);
}

void enableFieldsActive()
{
    inventJournalTable_ds.object(fieldNum(InventJournalTable, VoucherNumberSequenceTable)).mandatory(inventJournalTable.voucherNumberSequenceTableMandatory());
}

void init()
{
    xArgs args = this.args();

    // default journal type based on menu item name, this is to ensure query durable between caller.
    if (args && !args.parmEnumType())
    {
        switch (args.menuItemName())
        {
            case menuItemDisplayStr(InventJournalTableProject):
                args.parmEnumType(enumNum(InventJournalType));
                args.parmEnum(enum2int(InventJournalType::project));
                break;
        }
    }

    inventDimParmAllowCreate = true;
    journalFormTable = new InventJournalFormTable(element);

    // <GIN>
    if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
    {
        isInventJournalTable_INEnabled = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(InventJournalTable_IN));
    }
    // </GIN>

    super();

    journalFormTable.parmCtrlAllOpenPosted(allOpenPosted);
    journalFormTable.parmCtrlJournalTypeGrid(ctrlGridJournalType);
    journalFormTable.parmCtrlJournalIdTab(journalId);
    journalFormTable.parmCtrlJournalIdGrid(ctrlGridJournalId);
    journalFormTable.parmCtrlOverview(gridOverview);
    journalFormTable.parmCtrlMarkUserBlock(markUserBlock);
    journalFormTable.parmCtrlMarkInUseBlock(markInUseBlock);
    journalFormTable.parmCtrlSystemBlocked(systemBlocked);
    journalFormTable.parmCtrlLines(lines);
    journalFormTable.parmCtrlCheckJournal(checkJournal);
    journalFormTable.parmCtrlPostJournal(postJournal);
    journalFormTable.parmCtrlCreateNew(createNew);
    journalFormTable.parmCtrlInfolog(showInfoLog);
    journalFormTable.parmCtrlBlockImage(blockImage);
    journalFormTable.parmCtrlInitTotals(initTotals);
    journalFormTable.parmCtrlCopyJournal(inventJournalCopy);

    // <GEERU>
    journalFormTable.parmCtrlQueries_RU(queries_RU);
    journalFormTable.parmCtrlReports_RU(printMenu);
    // </GEERU>

    journalFormTable.formInitPost();
}

protected void initControls(Integer _journalTypeId)
{
    InventJournalType curType = any2Enum(_journalTypeId);

    switch (curType)
    {
        case InventJournalType::Movement:
                // <GEEPL>
                docTypePL.visible(true);
                docTypePL.allowEdit(true);
                // </GEEPL>
        case InventJournalType::LossProfit:
        case InventJournalType::BOM:
        case InventJournalType::project :
                inventCountingList.visible(false);
                inventJournalTag_CheckMissing.visible(false);
                employee.visible(false);
                voucher.visible(true);
                inventory.visible(true);
                inventDimParmView.visible(false);
                // <GEERU>
                if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoRU]))
                {
                    inventJournalTable_DS.object(fieldNum(InventJournalTable, Storno_RU)).visible(true);
                }
                // </GEERU>
                if (curType == InventJournalType::project)
                {
                    createNew.visible(false);
                }
            break;
        case InventJournalType::Count:
                inventJournalTag_CheckMissing.visible(false);
                employee.visible(true);
                voucher.visible(true);
                inventory.visible(true);
                inventDimParmView.visible(true);
                inventDimParmView.allowEdit(false);
            break;
        case InventJournalType::TagCounting:
                detailSummary.visible(false);
                inventCountingList.visible(false);
                employee.visible(true);
                voucher.visible(false);
                inventory.visible(false);
                inventDimParmView.visible(true);
                inventDimParmView.allowEdit(false);
            break;
        // <GEERU><GEEU>
        case InventJournalType::Transfer:
                inventCountingList.visible(false);
                inventJournalTag_CheckMissing.visible(false);
                employee.visible(false);
                voucher.visible(true);
                inventory.visible(true);
                inventDimParmView.visible(false);
                inventJournalTable_DS.object(fieldNum(InventJournalTable, Storno_RU)).visible(true);
            break;
        // </GEERU></GEEU>
    }
}

