void write()
{
    ttsbegin;

    super();

    journalFormTable.datasourceWritePost();
    // <GIN>
    if (isInventJournalTable_INEnabled && InventJournalTable_IN.InventJournalTable == 0)
    {
        // Write the extension table record now that the parent record has a record ID
        InventJournalTable_IN.InventJournalTable = InventJournalTable.RecId;
        InventJournalTable_IN.write();
    }
    // </GIN>

    ttscommit;
}

