public void dialogClosed(xFormRun _formRun)
{
    super(_formRun);

    ProjectIItemJournalExcelTemplateLookup lookupTemplate;

    if (_formRun.closedOk())
    {
        if (_formRun is ProjectIItemJournalExcelTemplateLookup)
        {
            lookupTemplate = _formRun as ProjectIItemJournalExcelTemplateLookup;
        }
        else
        {
            throw error(Error::wrongUseOfFunction(funcName()));
        }

        ProjectIItemJournalExcelTemplate journalTemplate = ProjectItemJournalExcelTemplate::constructForTemplateName(lookupTemplate.documentTemplateName());

        element.openExcelTemplate(journalTemplate);
    }
}

void clicked()
{
    MenuFunction    menuFunction;
    Args            args = new Args();

    switch (inventJournalTable.JournalType)
    {
        case InventJournalType::Transfer:
            menuFunction = new MenuFunction(menuitemOutputStr(InventJournalTransTransfer), MenuItemType::Output);
            break;
        case InventJournalType::TagCounting:
            menuFunction = new MenuFunction(menuitemOutputStr(InventJournalTrans_Tag), MenuItemType::Output);
            break;
        default:
            menuFunction = new MenuFunction(menuitemOutputStr(inventJournalTrans), MenuItemType::Output);
            break;
    }
    args.caller(element);
    args.record(inventJournalTable);
    args.menuItemName(menuFunction.name());
    args.menuItemType(menuFunction.type());
    menuFunction.run(args);
}

