public void enableCustVendFields_BR()
{
    boolean allowEdit;

    if (BrazilParameters::isEnabled())
    {
        allowEdit = (inventLocation.InventCountingGroup_BR != InventCountingGroup_BR::OwnStock);
        inventLocation_ds.object(fieldNum(InventLocation,CustAccount_BR)).allowEdit(allowEdit);
        inventLocation_ds.object(fieldNum(InventLocation,VendAccount)).allowEdit(allowEdit);
    }
}

public void enableFields()
{
    boolean     allowEditForNonWHSEnabledWarehouse;

    inventLocation_ds.object(fieldNum(InventLocation,InventLocationIdQuarantine)).allowEdit(inventLocation.InventLocationType == InventLocationType::Standard);
    inventLocation_ds.object(fieldNum(InventLocation,InventLocationIdTransit)).allowEdit(inventLocation.InventLocationType == InventLocationType::Standard);

    inventLocation_ds.object(fieldNum(InventLocation,Manual)).allowEdit(!inventLocation.isTransit());
    inventLocation_ds.object(fieldNum(InventLocation,ReqRefill)).allowEdit(!inventLocation.isTransit());
    inventLocation_ds.object(fieldNum(InventLocation,InventLocationIdReqMain)).allowEdit(!inventLocation.isTransit());

    allowEditForNonWHSEnabledWarehouse  = !inventLocation.whsEnabled;
    inventLocation_ds.object(fieldNum(InventLocation,wmsRackFormat)).allowEdit(inventLocation.wmsRackNameActive
    && allowEditForNonWHSEnabledWarehouse
    );

    inventLocation_ds.object(fieldNum(InventLocation,wmsLevelFormat)).allowEdit(inventLocation.wmsLevelNameActive
    && allowEditForNonWHSEnabledWarehouse
    );

    inventLocation_ds.object(fieldNum(InventLocation,wmsPositionFormat)).allowEdit(inventLocation.wmsPositionNameActive
    && allowEditForNonWHSEnabledWarehouse
    );

    inventLocation_ds.object(fieldNum(InventLocation,DefaultReturnCreditOnlyLocation)).allowEdit(!allowEditForNonWHSEnabledWarehouse);

    inventLocation_ds.object(fieldNum(InventLocation,wmSlocationIdDefaultIssue)).allowEdit(allowEditForNonWHSEnabledWarehouse);
    inventLocation_ds.object(fieldNum(InventLocation,MaxPickingRouteTime)).allowEdit(allowEditForNonWHSEnabledWarehouse);
    inventLocation_ds.object(fieldNum(InventLocation,MaxPickingRouteVolume)).allowEdit(allowEditForNonWHSEnabledWarehouse);
    inventLocation_ds.object(fieldNum(InventLocation,PickingLineTime)).allowEdit(allowEditForNonWHSEnabledWarehouse);
    inventLocation_ds.object(fieldNum(InventLocation,wmsAisleNameActive)).allowEdit(allowEditForNonWHSEnabledWarehouse);
    inventLocation_ds.object(fieldNum(InventLocation,wmsRackNameActive)).allowEdit(allowEditForNonWHSEnabledWarehouse);
    inventLocation_ds.object(fieldNum(InventLocation,wmsLevelNameActive)).allowEdit(allowEditForNonWHSEnabledWarehouse);
    inventLocation_ds.object(fieldNum(InventLocation,wmsPositionNameActive)).allowEdit(allowEditForNonWHSEnabledWarehouse);
    MaintainButtonGroup.enabled(allowEditForNonWHSEnabledWarehouse);

    this.enableWHSManagementParameters();

    inventLocation_ds.object(fieldNum(InventLocation, InventLocationIdGoodsInRoute_RU)).allowEdit(inventLocation.InventLocationType != InventLocationType::GoodsInRoute_RU);
    inventLocation_ds.object(fieldNum(InventLocation, wmsLocationIdGoodsInRoute_RU)).allowEdit(inventLocation.InventLocationType == InventLocationType::GoodsInRoute_RU);

    this.enableCustVendFields_BR();

    inventLocation_ds.object(fieldNum(InventLocation, RemoveInventBlockingOnStatusChange))
        .allowEdit(inventLocation.allowRemoveBlockingOnStatusChanged());

    example.update();

    inventLocation_ds.object(fieldNum(InventLocation, LoadReleaseReservationPolicy)).visible(InventLocation.ReserveAtLoadPost);

    inventLocation_ds.object(fieldNum(InventLocation, ConsolidateShipAtRTW)).visible(inventLocation.ConsolidateShipAtRTW);
}

/// <summary>
/// Updates the caller data sources if the counting reason code policy is updated.
/// </summary>
public void updateCallerDatasources()
{
    InventLocation_ds.refresh();
    InventLocation_ds.active();

    InventLocationInventCountingReasonCodePolicy_ds.refresh();
    InventLocationInventCountingReasonCodePolicy_ds.active();
}

public void enableReplenishmentGroup()
{
    if (inventLocation.fshStore == NoYes::Yes)
    {
        inventLocation_ds.object(fieldNum(InventLocation, RetailWeightEx1)).allowEdit(true);
    }
    else
    {
        inventLocation.RetailWeightEx1 = 0.00;
        inventLocation_ds.object(fieldNum(InventLocation, RetailWeightEx1)).allowEdit(false);
    }
}

/// <summary>
/// Toggle warehouse management parameter fields based on whether the warehouse
/// management processes is enabled or not.
/// </summary>
public void enableWHSManagementParameters()
{
    boolean whsEnabled = inventLocation.whsEnabled;

    inventLocation_ds.object(fieldNum(InventLocation, PrintBOLBeforeShipConfirm)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, ProdReserveOnlyWhse)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, WarehouseAutoReleaseReservation)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, ReserveAtLoadPost)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, LoadReleaseReservationPolicy)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, CycleCountAllowPalletMove)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, UniqueCheckDigits)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, DecrementLoadLine)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, ConsolidateShipAtRTW)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, AllowLaborStandards)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, ReleaseToWarehouseRule)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, WorkProcessingPolicyName)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, AutoUpdateShipment)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, DefaultQualityMaintenanceLocation)).allowEdit(whsEnabled);
    inventLocation_ds.object(fieldNum(InventLocation, EnableQualityManagement)).allowEdit(whsEnabled);
}

