/// <summary>
/// Returns an instance of RetailReplenishmentTreeDatasource for the current form.
/// </summary>
/// <returns>
/// The new instance of <c>RetailReplenishmentTreeDatasource</c> class.
/// </returns>
public RetailReplenishmentTreeDatasource getRetailReplenishmentTreeDataSource()
{
    return new RetailReplenishmentTreeDatasource(omHierarchyrelationship_ds,
                                retailReplenishmentTree,
                                fieldNum(OMHierarchyRelationship, ChildOrganization),
                                fieldNum(OMHierarchyRelationship, ParentOrganization),
                                NoYes::Yes);
}

public void init()
{
    super();
    postalAddressForm = LogisticsEntityPostalAddressFormHandler::newParameters(inventLocation_ds, logisticsEntityPostalAddressView_ds);

    RetailReplenishmentTree.visible(false);

    if (isConfigurationkeyEnabled(configurationKeyNum(Retail)))
    {
        hierarchyTree = this.getRetailReplenishmentTreeDataSource();
    }

    if (BrazilParameters::isEnabled())
    {
        detailAddress.enabled(false);
    }
}

/// <summary>
