/// <summary>
/// On activation of Hierarchy Tree tab page
/// </summary>
public void pageActivated()
{
    super();

    buttonExpandTree.text("@SYS22914");
    buttonExpandTree.helpText("@SYS104161");
    formTreeExpand   = FormTreeExpand::Collapse;
    siteIdxContainer = InventSite::fillHierarchyTree(inventLocation.InventSiteId, formTreeControl);
}

/// <summary>
/// On click of the expand/collapse hierarchy tree button
/// </summary>
public void clicked()
{
    int             i;

    super();

    if (formTreeExpand == FormTreeExpand::Collapse)
    {
        buttonExpandTree.text("@SYS104162");
        buttonExpandTree.helpText("@SYS104163");
        formTreeExpand = FormTreeExpand::Expand;
    }
    else
    {
        buttonExpandTree.text("@SYS22914");
        buttonExpandTree.helpText("@SYS104161");
        formTreeExpand = FormTreeExpand::Collapse;
    }

    for (i = 1; i <= conLen(siteIdxContainer); i++)
    {
        formTreeControl.expand(conPeek(siteIdxContainer, i), formTreeExpand);
    }
}

/// <summary>
/// Builds a subtree for the tree node specified.
/// </summary>
/// <param name="_idx">
/// The index of the tree node.
/// </param>
/// <param name="_action">
/// FormTreeExpand action.
/// </param>
/// <param name="_data">
/// The data of the tree node.
/// </param>
/// <returns>
/// true, if expand is successful, otherwise - false
/// </returns>
public boolean expanding(int _idx, FormTreeExpand _action, anytype _data)
{
    boolean ret;

    ret = super(_idx, _action, _data);

    if (hierarchyTree)
    {
        hierarchyTree.expanding(_idx, _action, _data);
    }

    return ret;
}

/// <summary>
/// If the store checkbox is checked, show the warning if this warehouse is associated with
/// a WMSLocation that is a user location.
/// </summary>
public void clicked()
{
    super();

    if (this.checked())
    {
        // Verify if this warehouse has a user WMSLocation associated.
        InventLocation::validateNoWHSUserLocation(inventLocation.InventLocationId);
    }
}

public boolean modified()
{
    boolean ret = super();

    inventLocation_ds.object(fieldNum(InventLocation, LoadReleaseReservationPolicy)).visible(InventLocation.ReserveAtLoadPost);

    return ret;
}
