[Form]
public class InventLocationIdLookup extends FormRun
{
    InventDimCtrl_Frm_Lookup    inventDimFormSetup;
    InventDimensionWithOnHandLookup inventDimensionWithOnHandLookup;

    // Packed
    boolean                     showAvail;
    boolean                     linkDim;    // Must link on all dimensions in caller form
    boolean                     showItemInfo;
    boolean                     onHandViewIsSelected;
    boolean                     isCrossCompanyQuery;

    InventAvailabilityFormCache inventAvailabilityFormCache;
    Map                         tab2ComboBoxItemMap;

    #define.CurrentVersion(1)
    #localmacro.CurrentList
            ShowAvail,
            LinkDim
    #endmacro

}

private void setSelectMode()
{
    inventDimensionWithOnHandLookup.setSelectMode();
    this.selectMode(inventDimensionWithOnHandLookup.getSelectedControl());
}

private void lookupTabChanged()
{
    inventDimensionWithOnHandLookup.lookupTabChanged();
}

InventLocationId callerInventLocationId()
{
    FieldId     inventLocationFieldId;
    Common      common;

    inventLocationFieldId = inventDimFormSetup.callerInventLocationFieldId();

    if (!inventLocationFieldId)
    {
        return '';
    }

    common = inventDimFormSetup.callerInventLocationIdCursor();
    if (common.TableId == tableNum(Common))
    {
        return '';
    }

    return common.(inventLocationFieldId);
}

InventLocationId callerInventSiteId()
{
    FieldId     inventSiteFieldId;
    Common      common;

    inventSiteFieldId = inventDimFormSetup.callerInventSiteFieldId();

    if (!inventSiteFieldId)
    {
        return '';
    }

    common = inventDimFormSetup.callerInventSiteIdCursor();
    if (common.TableId == tableNum(Common))
    {
        return '';
    }

    return common.(inventSiteFieldId);
}

void close()
{
    xSysLastValue::saveLast(this);

    super();
}

void init()
{
    InventLocationId    inventLocationId;

    super();

    FormStringControl callerControl = SysTableLookup::getCallerStringControl(element.args());

    element.updateDesign(InventDimFormDesignUpdate::Init);

    inventDimFormSetup.initInventDimQueryDatasource(inventDim_ds);
    inventDimFormSetup.initInventSumQueryDatasource(inventSum_ds);

    isCrossCompanyQuery = this.getAllowCrossCompany();

    xSysLastValue::getLast(this);
    ctrlShowAvail.value(showAvail);
    element.setControlsVisibility();

    if (inventDimFormSetup.hasCallerInventDimParm())
    {
        ctrlLinkDim.value(linkDim);
    }
    else
    {
        linkDim = false;
        ctrlLinkDim.visible(false);
    }

    Common recordToPosition;
    if (callerControl && callerControl.text() && !callerControl.hasChanged())
    {
        inventLocationId = element.callerInventLocationId();
        if (inventLocationId)
        {
            recordToPosition = InventLocation::find(inventLocationId);
        }
    }

    Map lookupTabCollection = new Map(Types::Integer, Types::Class);

    lookupTabCollection.insert(1, new InventDimensionWithOnHandLookupTab(1, ctrlInventLocation_InventLocationId, inventLocation_ds));
    lookupTabCollection.insert(2, new InventDimensionWithOnHandLookupTab(2, ctrlInventDimItem_InventLocationId, inventDim_ds));

    inventDimensionWithOnHandLookup = InventDimensionWithOnHandLookup::newWithTabCollection(
        callerControl,
        ctrlTab,
        lookupTabCollection,
        recordToPosition
        );

    inventAvailabilityFormCache = InventAvailabilityFormCache::construct();

    tab2ComboBoxItemMap = SysLookup::tab2ComboBox(ctrlTab, switchView);
}

private boolean getAllowCrossCompany()
{
    boolean isCrossCompany;

    FormDataSource callerFormDatasource = inventDimFormSetup.callerFormDatasource();
    if (callerFormDatasource)
    {
        if (callerFormDatasource.table() == tableNum(InventDim) && callerFormDatasource.query())
        {
            isCrossCompany = callerFormDatasource.query().allowCrossCompany();
        }
    }

    return isCrossCompany;
}

