}

void run()
{
    this.setSelectMode();

    boolean filterLookup = inventDimensionWithOnHandLookup.filterLookupPreRun();

    this.setShowAvail();

    super();

    inventDimensionWithOnHandLookup.filterLookupPostRun(filterLookup);
}

void setShowAvail()
{
    ctrlInventSum_AvailOrdered.visible(ctrlShowAvail.value());
    ctrlInventSum_AvailPhysical.visible(ctrlShowAvail.value());
    ctrlPhysicalInvent.visible(!ctrlShowAvail.value());
}

private void setControlsVisibility()
{
    ctrlShowAvail.visible(ctrlTabPageItem.isActivePage());
    ctrlLinkDim.visible(ctrlTabPageItem.isActivePage());
}

public boolean unpack(container _packedClass)
{
    Integer     tmpVersion     = conPeek(_packedClass,1);
    Integer     tab;

    switch (tmpVersion)
    {
        case #CurrentVersion :
            [tmpVersion,#CurrentList,tab] = _packedClass;
            break;
        default :
            return false;
    }

    if (!showItemInfo && tab == 2)
    {
        ctrlTab.tab(1);
    }
    else
    {
        ctrlTab.tab(tab);
    }

    this.setControlsVisibility();

    return true;
}

void updateDesign(InventDimFormDesignUpdate   _mode)
{
    showItemInfo = true;
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init:
            if (! inventDimFormSetup)
            {
                if (element.args().dataset() == tableNum(TmpSysQuery))
                {
                    inventDimFormSetup  = InventDimCtrl_Frm_Lookup_TmpSysQuery::newFromFormSortField(element,fieldNum(InventDim,InventLocationId));
                }
                else
                {
                    inventDimFormSetup  = InventDimCtrl_Frm_Lookup::newFromFormSortField(element,fieldNum(InventDim,InventLocationId));
                }
            }
            if (! inventDimFormSetup.hasCallerItemId())
            {
                showItemInfo = false;
                ctrlTabPageItem.visible(false);
                ctrlTab.showTabs(false);
                ctrlGroupSetup.visible(false);
            }
            else
            {
                inventDimFormSetup.formSetControls(true, true);
            }
            break;
        case InventDimFormDesignUpdate::LinkActive:
            inventDimFormSetup.formActiveSetup();
            inventDimFormSetup.formSetControls(true,true);
            break;
        default:
            throw error(strFmt("@SYS54195",funcName()));
    }
}

void executeQuery()
{
    QueryBuildDataSource    qbdsInventLocation;
    DictRelation            dr;
    Counter                 i;
    FormDataSource          callerFormDatasource;
    DictType                dt;
    ExtendedTypeId          extendedTypeId;
    FieldId                 inventSiteFieldId;
    Common                  cursor;

    //if queryRun object is created use query from it
    //this allows to modify query that is used in filtered lookups
    if (this.queryRun())
    {
        qbdsInventLocation = this.queryRun().query().dataSourceTable(tableNum(InventLocation));
    }
    else
    {
        qbdsInventLocation = this.query().dataSourceTable(tableNum(InventLocation));
    }

    inventSiteFieldId = inventDimFormSetup.callerInventSiteFieldId();

    qbdsInventLocation.clearDynalinks();

    if (inventSiteFieldId)
    {
        cursor = inventDimFormSetup.callerInventSiteIdCursor();

        if (cursor.TableId != tableNum(Common) && cursor.(inventSiteFieldId))
        {
            qbdsInventLocation.addDynalink(
                fieldNum(InventLocation,InventSiteId),
                cursor,
                inventSiteFieldId);
        }
    }

    FormStringControl ctrl = element.selectTarget() as FormStringControl;
    if (ctrl)
    {
        extendedTypeId = ctrl.extendedDataType();
        if (extendedTypeId)
        {
            dt = new DictType(extendedTypeId);
            dr = dt.relationObject();
        }
        else
        {
            callerFormDatasource    = inventDimFormSetup.callerFormDatasource();
            if (callerFormDatasource)
            {        
                dr = new DictRelation(callerFormDatasource.table());
                dr.loadFieldRelation(ctrl.dataField());
            }
        }
    }
    if (dr)
    {
        for (i=1;i<=dr.lines();i++)
        {
            switch (dr.lineType(i))
            {
                case TableRelation::ExternFixed:
                    qbdsInventLocation.addRange(dr.lineExternTableValue(i)).value(queryValue(dr.lineTableValue(i)));
                    break;
            }
        }
    }

    super();
}

/// <summary>
/// Builds the query for the datasource.
/// </summary>
/// <returns>The query.</returns>
protected Query buildQuery()
{
    Query query = new Query();
    QueryBuildDataSource qbds = query.addDataSource(tableNum(InventLocation));
    qbds.addSortField(fieldNum(InventLocation, InventLocationId));

    // <GEERU>
    if (element.args() && element.args().dataset() == tableNum(CustomJournalTable_RU))
    {
        CustomJournalTable_RU customJournalTable = element.args().record();

        QueryBuildRange queryBuildRange = qbds.addRange(fieldNum(InventLocation, VendAccountCustom_RU));

        if (customJournalTable.VendAccountCustom)
        {
            queryBuildRange.value(customJournalTable.VendAccountCustom);
        }
        else
        {
            queryBuildRange.value(SysQuery::valueNotEmptyString());
        }

        queryBuildRange.status(RangeStatus::Locked);
    }
    // </GEERU>

    return query;
}

void init()
{
    super();

    this.query(this.buildQuery());
}

public display InventQtyAvailPhysical availPhysical(InventDim _inventDim)
{
    return inventAvailabilityFormCache.availabilityFromDimJoinedWithSum(_inventDim, inventSum_ds).availPhysical();
}

public display InventQtyAvailOrdered availOrdered(InventDim _inventDim)
{
    return inventAvailabilityFormCache.availabilityFromDimJoinedWithSum(_inventDim, inventSum_ds).availTotal();
}

/// <summary>
/// Builds the query for the datasource.
/// </summary>
/// <returns>The query.</returns>
protected Query buildQuery()
{
    Query query = new Query();
    QueryBuildDataSource qbds = query.addDataSource(tableNum(InventDim));
    qbds.name(inventDim_ds.name());

    return query;
}

void init()
{
    super();

    this.query(this.buildQuery());
}

void executeQuery()
{
    if (inventDimFormSetup.hasCallerItemId())
    {
        inventDimFormSetup.executeInventDimQueryDatasource(this,
                                                           linkDim,
                                                           showAvail
                                                          );

        element.updateDesign(InventDimFormDesignUpdate::LinkActive);

        inventAvailabilityFormCache.refreshCache();

        this.query().allowCrossCompany(isCrossCompanyQuery);

        super();
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    ctrlTab.tabChanged(ctrlTab.tabValue(), tab2ComboBoxItemMap.lookup(this.selection()));

    element.setControlsVisibility();

    element.setSelectMode();

    element.lookupTabChanged();

    return ret;
}

void clicked()
{
    super();

    showAvail = ! showAvail;

    inventDim_ds.executeQuery();

    element.setShowAvail();
}

void clicked()
{
    super();

    linkDim = !linkDim;

    inventDim_ds.executeQuery();
}
