[Form]
public class InventNonConformanceTable extends FormRun
{
    InventNonConformanceID                  createId;

    boolean                                 inCreateFromForm;

    InventDimCtrl_Frm_NonConformance        inventDimFormSetup;

    PdsCWFormCtrl                           cwFormCtrl;

    InventTrackingNonConformSummaryFilter   traceFilter;

}

InventNonConformanceID createId(InventNonConformanceID _createId = createId)
{
    createId = _createId;
    return createId;
}

void executeOnActive()
{
    element.setOperationControls();
    inventNonConformanceTable_ds.allowEdit(!inventNonConformanceTable.Closed);
    inventDim_ds.allowEdit(!inventNonConformanceTable.Closed);
    element.setAvailFields();

    if (element.args())
    {
        switch (element.args().dataset())
        {
            case tableNum(PurchTable):
            case tableNum(SalesTable):
                inventNonConformanceTable_ds.allowCreate(false);
                break;
        }
    }
}

public void init()
{
    if (!InventParameters::find().UseQualityManagement)
    {
        throw error("@SYS114218");
    }

    cwFormCtrl = PdsCWFormCtrl_Min::construct();
    cwFormCtrl.parmFormRun(this);
    cwFormCtrl.parmFieldIds(PdsCWValidation::fieldMapFromContainers(
        [[fieldNum(InventNonConformanceTable, TestDefectQty),
          fieldNum(InventNonConformanceTable, PdsCWTestDefectQty),
          false,
          fieldNum(InventNonConformanceTable, UnitId)
        ]]
        ));
    cwFormCtrl.parmItemFieldId(fieldNum(InventNonConformanceTable, ItemId));
    cwFormCtrl.initPre();

    traceFilter = element.args().parmObject() as InventTrackingNonConformSummaryFilter;
    if (traceFilter)
    {
        traceFilter.parmFormRun(this);
    }

    super();

    if (traceFilter)
    {
        traceFilter.formInitPost();
        itemTracingRelations.visible(true);
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    cwFormCtrl.initPost();
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init        :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl_Frm_NonConformance::newFromForm(element);
            }

        case InventDimFormDesignUpdate::Active      :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(inventNonConformanceTable.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        case InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(inventNonConformanceTable.ItemId));
            inventDimFormSetup.formSetControls(false);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

void launchForm()
{
    Args args = new Args();
    args.caller(this);
    MenuItemName menuItemName;

    switch (inventNonConformanceTable.InventNonConformanceType)
    {
        case InventNonConformanceType::Cust:
        case InventNonConformanceType::Service:
            args.record(SalesTable::find(InventNonConformanceTable.InventRefId));
            menuItemName = menuItemDisplayStr(SalesTable);
            break;
        case InventNonConformanceType::Vend:
            args.record(PurchTable::find(InventNonConformanceTable.InventRefId));
            menuItemName = menuItemDisplayStr(PurchTable);
            break;
        case InventNonConformanceType::Production:
            args.record(ProdTable::find(InventNonConformanceTable.InventRefId));
            menuItemName = menuItemDisplayStr(ProdTable);
            break;
        case InventNonConformanceType::Internal:
            args.record(InventQualityOrderTable::find(InventNonConformanceTable.InventRefId));
            menuItemName = menuItemDisplayStr(InventQualityOrderTable);
            break;
        default: 
            return;
    }

    new MenuFunction(menuItemName, MenuItemType::Display).run(Args);
}

/// <summary>
/// Initializes the <c>InventNonConformanceTable</c> table query based on the table types provided in the form args.
/// </summary>
protected void initInventNonConformanceTableQuery()
{
    QueryBuildDataSource    qbds;
    QueryBuildDataSource    qbdsOrigin;

    switch (element.args().dataset())
    {
        case tableNum(InventSum):
            qbds = inventNonConformanceTable_ds.query().dataSourceTable(tableNum(InventNonConformanceTable));
            qbds.clearDynalinks();
            qbds.addDynalink(fieldNum(InventNonConformanceTable, ItemId), element.args().record(), fieldNum(InventSum, ItemId));
            break;
        case tableNum(InventBatch):
            qbds = inventNonConformanceTable_ds.query().dataSourceTable(tableNum(InventNonConformanceTable));
            qbds.clearDynalinks();
            qbds.addDynalink(fieldNum(InventNonConformanceTable, ItemId), element.args().record(), fieldNum(InventBatch, ItemId));
            break;
        case tableNum(InventQualityOrderTable):
            qbds = inventNonConformanceTable_ds.query().dataSourceTable(tableNum(InventNonConformanceTable));
            qbds.clearRanges();
            qbds.clearDynalinks();
            qbdsOrigin = qbds.addDataSource(tableNum(InventNonConformanceOrigin));
            qbdsOrigin.joinMode(JoinMode::ExistsJoin);
            qbdsOrigin.relations(true);
            qbdsOrigin.addDynalink(fieldNum(InventNonConformanceOrigin, InventQualityOrderId), element.args().record(), fieldNum(InventQualityOrderTable, QualityOrderId));
            break;
    }
}

/// <summary>
/// Returns the <c>Args</c> for opening the form to create new <c>inventNonConformanceTable</c> records.
/// </summary>
/// <returns>The <c>Args</c> class.</returns>
protected Args inventNonConformanceTableCreateArgs()
{
    Args args = new Args();
    args.name(formStr(InventNonConformanceTableCreate));
    args.caller(element);

    if (element.args() &&
            element.args().record() &&
            element.args().record().RecId)
    {
        args.record(element.args().record());
    }
    return args;
}

public void write(boolean _onlySuper = false)
{
    if (_onlySuper)
    {
        inCreateFromForm = true;
        super();
        inCreateFromForm = false;
    }
    else
    {
        ttsbegin;

        inventNonConformanceTable.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

        super();

        if (inventNonConformanceTable.InventDimId != inventDim.InventDimId)
        {
            inventDim.data(InventDim::find(inventNonConformanceTable.InventDimId));
            inventDim_ds.setCurrent();
        }

        ttscommit;
    }

    inventNonConformanceTable_ds.reread();
    inventNonConformanceTable_ds.refresh();
}

public void create(
    boolean                 _append     = false,
    boolean                 _onlySuper  = false,
    InventNonConformanceID  _inventNonConformanceIdRelated = '')
{
    if (_onlySuper)
    {
        super(_append);
    }
    else
    {
        createId = '';

        FormRun formRun = classfactory.formRunClass(element.inventNonConformanceTableCreateArgs());
        formRun.init();
        formRun.run();
        formRun.wait();

        if (inventNonConformanceTable.RecId)
        {
            inventNonConformanceTable.reread();
        }

        if (createId)
        {
            element.setActive();

            super(_append);
            inventNonConformanceTable.data(InventNonConformanceTable::find(createId));
            inventDim.data(inventNonConformanceTable.inventDim());
            inventNonConformanceTable_ds.reread();
            hcmWorker_ds.reread();

            if (_inventNonConformanceIdRelated)
            {
                InventNonConformanceRelation inventNonConformanceRelation;
                inventNonConformanceRelation.InventNonConformanceID = inventNonConformanceTable.InventNonConformanceID;
                inventNonConformanceRelation.InventNonConformanceIDRef = _inventNonConformanceIdRelated;
                inventNonConformanceRelation.insert();
            }
        }
    }
}

public int active()
{
    int ret;

    ret = super();

    element.executeOnActive();
    element.updateDesign(InventDimFormDesignUpdate::Active);

    cwFormCtrl.dataSourceActivePost();
    return ret;
}

public void init()
{
    super();

    if (traceFilter)
    {
        traceFilter.parmMainDataSource(InventNonConformanceTable_ds);
        traceFilter.parmFilterDataSource(InventNonConformanceTmpFilter_ds);
        traceFilter.dataSourceInitPost();
    }
    else if (element.args())
    {
        element.initInventNonConformanceTableQuery();
    }

    inventNonConformanceTable_ds.query().dataSourceNo(1).addSortField(fieldNum(InventNonConformanceTable, InventNonConformanceID));

    inventNonConformanceTable_ds.cacheAddMethod(tableMethodStr(InventNonConformanceTable, reportedBy));
    inventNonConformanceTable_ds.cacheAddMethod(tableMethodStr(InventNonConformanceTable, approvedBy));
    inventNonConformanceTable_ds.cacheAddMethod(tableMethodStr(InventNonConformanceTable, qualityOrderId));
    inventNonConformanceTable_ds.cacheAddMethod(tableMethodStr(InventNonConformanceTable, showProblemDesc));
    inventNonConformanceTable_ds.cacheAddMethod(tableMethodStr(InventNonConformanceTable, costAmount));
    cwFormCtrl.dataSourceInitPost(inventNonConformanceTable);
}

public boolean validateWrite()
{
    boolean ret;

    if (inCreateFromForm)
    {
        return true;
    }

    ret = super();

    ret = ret && cwFormCtrl.dataSourceValidateWrite();
    return ret;
}

public void init()
{
    QueryBuildDataSource    qbds;
    super();

    if (element.args() && element.args().dataset() == tableNum(InventBatch))
    {
        qbds = inventNonConformanceTable_ds.query().dataSourceTable(tableNum(InventDim));
        qbds.clearDynalinks();
        qbds.addDynalink(fieldNum(InventDim, InventBatchId), element.args().record(), fieldNum(InventBatch, InventBatchId));
    }
}

void clicked()
{
    inventNonConformanceApprove.enabled(    inventNonConformanceTable.RecId
                                        &&  inventNonConformanceTable.InventNonConformanceApproval != InventNonConformanceApproval::Approved
                                        && !inventNonConformanceTable.Closed);

    inventNonConformanceRefuse.enabled(     inventNonConformanceTable.RecId
                                        &&  inventNonConformanceTable.InventNonConformanceApproval != InventNonConformanceApproval::Refused
                                        && !inventNonConformanceTable.Closed
                                        && !inventNonConformanceTable.correctionsExist());

    inventNonConformanceClose.enabled(      inventNonConformanceTable.RecId
                                        && !inventNonConformanceTable.Closed);

    inventNonConformanceReOpen.enabled(     inventNonConformanceTable.RecId
                                        &&  inventNonConformanceTable.Closed);

    generateRelatedNCButton.enabled(        inventNonConformanceTable.RecId);

    inventNonConformanceMakeRelated.enabled(inventNonConformanceTable.RecId);

    inventNonConformanceChangeType.enabled( inventNonConformanceTable.RecId
                                        && !inventNonConformanceTable.Closed);
    super();
}
