public void applyUnitID()
{
    UnitOfMeasureSymbol tempUnitID;

    // If we don't have an item, then units don't matter
    if (!inventSum.ItemId)
    {
        return;
    }

    boolean allowEditUnit;
    [tempUnitID, allowEditUnit] = InventOnhand::inventModuleUnitsSelectionToUnitIdAllowEdit(unitsComboBox.selection(), inventSum.ItemId, unitIdSecondary);
    ctrlUnitId.allowEdit(allowEditUnit);

    // Units may be empty, if so fall back to inventory units
    if (!tempUnitID)
    {
        unitsComboBox.selection(InventModuleUnits::InventoryUnit);
        tempUnitID = unitIdInvent;
        info("@SYS113267");
    }

    // Apply new unit
    element.unitId(true, tempUnitID);
}

private InventDim currentInventDim()
{
    InventDim inventDimLocal;

    InventDim.toInventDim(inventDimLocal);
    inventDimLocal = InventDim::findOrCreate(inventDimLocal);

    return inventDimLocal;
}

private boolean compareProductDimensions(
    InventDim _inventDim,
    InventDim _compareInventDim)
{
    EcoResProductDimGroupSetup ecoResProductGroup = EcoResProductDimGroupSetup::newItemId(inventSum.ItemId);

    return InventDim::isInventDimEqualProductDim(ecoResProductGroup, _inventDim, _compareInventDim);
}

